nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    if (immutableData['ONMC.checklist.allCheck'] === true) {
        updatedData["ONMC.checklist.medsFromOthers"] = "Completed";
        updatedData["ONMC.checklist.medsFromHome"] = "Completed";
        updatedData["ONMC.checklist.OTC"] = "Completed";
        updatedData["ONMC.checklist.naturalHealthProducts"] = "Completed";
        updatedData["ONMC.checklist.forgottenDosageForms"] = "Completed";
        updatedData["ONMC.checklist.antiInfectives"] = "Completed";
        updatedData["ONMC.checklist.references"] = "Completed";
        updatedData["ONMC.checklist.circleOfCare"] = "Completed";
        updatedData["ONMC.checklist.anticipatedDate"] = "Completed";
        updatedData["ONMC.checklist.clinicallyRelevant"] = "Completed";
    }
    
    // logic to reset "Completed" bindings to false, in the event that allCheck is set to false
    if (immutableData['ONMC.checklist.allCheck'] === false) {
        let completenessArray = ["medsFromOthers", "medsFromHome", "OTC", "naturalHealthProducts", "forgottenDosageForms", "antiInfectives", "references", "circleOfCare", "anticipatedDate", "clinicallyRelevant"]; 
        
        for (let i = 0; i < completenessArray.length; i++) {
            if (immutableData["ONMC.checklist." + completenessArray[i] ] === "Completed" ) {
                updatedData["ONMC.checklist." + completenessArray[i]] = false;
            }
        }
    }
    
    // backtracking code for "Other"
    if(immutableData["ONMC.checklist.other"] === false){
        updatedData["ONMC.checklist.otherNotes"] = undefined;
    }
    
    //Show/Hide for 8 options
    /*
        The maximum that can be displayed is 6 options , the logic will be validated if they should be shown and if they can be, logic will be done to ensure only 6 are shown.
    */
    var serviceNewOrCon = immutableData["service.continuedOrNew"] ? immutableData["service.continuedOrNew"][0] : undefined;
    var haspcp = immutableData["enableFormPCPFlow"] ? immutableData["enableFormPCPFlow"] : false; 
    if (serviceNewOrCon === "New Service" && haspcp) {
         
        var advFlow = immutableData["ONMC.patient.followUpAdvanceFlow"] ? immutableData["ONMC.patient.followUpAdvanceFlow"][0] : "No";
        var suggested = ["HYPERGlycemia"];
        var displayHypo = '<h3>HYPOGlycemia</h3>';
        var displayFoot = '<h3>Footcare </h3>';
        var displayEye = '<h3>Eye Health </h3>';
        var displaySelf = '<h3>Self-monitoring of Blood Glucose and Glucose Targets</h3>';
        var displaySickDay = '<h3>Sick Day Management</h3>';
        var displayVaccine = '<h3>Vaccinations</h3>';
        var displayInjection = '<h3>Injections</h3>';
        var displayPOCT = '<h3>Diabetes Targets and POCT</h3>';
        var displayNutrition = '<h3>Nutrition</h3>';
        var displayHyper = '<h3>HYPERGlycemia</h3>';
        var showHypo = false;
        var showFoot = false;
        var showEye = false;
        var showSelf = false;
        var showSickDay = false;
        var showNutrition = false;
        var showVaccine = false;
        var showInjection = false;
        var showPOCT = false;
        var showHyper = true;
        var nutritionCheck = immutableData["ONMC.lifestyle.patientDietCheck"] ? immutableData["ONMC.lifestyle.patientDietCheck"] : false;
        if (nutritionCheck === true ) {showNutrition = true;suggested.push("Nutrition");}
        
        var vaccine = immutableData["showVaccineInfo"] ? immutableData["showVaccineInfo"][0] : undefined ; 
        if (vaccine !== "Yes") {showVaccine = true ; suggested.push("Vaccinations");}
        
        
        
        if (advFlow === "Yes"){
        var maxTopics = 0;
        
        var hypoLogic = immutableData["ONMC.lifestyle.hyGlyRisk"] ? immutableData["ONMC.lifestyle.hyGlyRisk"][0] : undefined ; 
        if(hypoLogic === "Yes") {showHypo = true ; suggested.push("HYPOGlycemia"); }
        
        var optionalScreening = immutableData["showOptionalScreeningSection"] ? immutableData["showOptionalScreeningSection"][0] : undefined ; 
        var footLogic = immutableData["ONMC.lifestyle.footRoutine"] ? immutableData["ONMC.lifestyle.footRoutine"][0] : undefined ; 
        if (footLogic === "No" || optionalScreening === "No") {showFoot = true; suggested.push("Foot Care");}
        
        var eyeLogic = immutableData["ONMC.lifestyle.visionAppt"] ? immutableData["ONMC.lifestyle.visionAppt"][0] : undefined ; 
        if (eyeLogic === "No" || optionalScreening === "No") {showEye = true; suggested.push("Eye Health");}
        
        var selfLogic = immutableData["ONMC.lifestyle.bgMonitor"] ? immutableData["ONMC.lifestyle.bgMonitor"][0] : undefined ; 
        if (selfLogic === "No") {showSelf = true ; suggested.push("Self-monitoring of Blood Glucose");}
        
        var sickDay = immutableData["ONMC.lifestyle.sdPlan"] ? immutableData["ONMC.lifestyle.sdPlan"][0] : undefined ; 
        if (sickDay === "No" || optionalScreening === "No") {showSickDay = true;suggested.push("Sick Day Management");}
        
       
        
        var injSite = immutableData["ONMC.lifestyle.injSit"] ? immutableData["ONMC.lifestyle.injSit"][0] : undefined ; 
        if (injSite === "No" || optionalScreening === "No") {showInjection = true;suggested.push("Injections");}
        
        
        
        var labWork = immutableData["ONMC.lifestyle.labWork"] ? immutableData["ONMC.lifestyle.labWork"][0]:undefined;
        if (labWork === "No" || optionalScreening === "No") {showPOCT = true; suggested.push("Diabetes Targets and POCT");}
        
        console.log("Show hypo: \t " + showHypo )
        console.log("Show showFoot: \t " + showFoot )
        console.log("Show showEye: \t " + showEye )
        console.log("Show showSelf: \t " + showSelf )
        console.log("Show Hyper: \t " + showHyper )
        console.log("Show showSickDay: \t " + showSickDay )
        console.log("Show Nutrition: \t " + showNutrition )
        console.log("Show Vaxx: \t " + showVaccine )
        console.log("Show InjSite: \t " + showInjection )
        
        
        console.log("Show POCT: \t " + showPOCT )
        
        var rank = ["HYPOGlycemia","Foot Care","Eye Health", "Self-monitoring of Blood Glucose","HYPERGlycemia","Sick Day Management","Nutrition","Vaccinations","Injections","Diabetes Targets and POCT"];
        
        for (var i = 0 ; i < rank.length && maxTopics < 6 ; i++) {
            if(maxTopics > 6){
                break;
            } else { 
            
            switch(rank[i]){
                case "HYPOGlycemia":
                    if(showHypo){
                        displayHypo = "<span class='blue'>Suggested Topic</span><br/>" + displayHypo;
                        maxTopics++;
                    }
                    break;
                case "Foot Care":
                    if(showFoot){
                        displayFoot = "<span class='blue'>Suggested Topic</span><br/>" + displayFoot; 
                        maxTopics++;
                    }
                    break;
                case "Eye Health":
                    if(showEye){
                        displayEye = "<span class='blue'>Suggested Topic</span><br/>" + displayEye;
                        maxTopics++;
                    }
                    break;
                case "Self-monitoring of Blood Glucose":
                    if(showSelf){
                        displaySelf = "<span class='blue'>Suggested Topic</span><br/>" + displaySelf;
                        maxTopics++;
                    }
                    break;
                case "HYPERGlycemia":
                    if(showHyper === true){
                            displayHyper = "<span class='blue'>Suggested Topic</span><br/>" + displayHyper;
                            maxTopics++;
                        }
                    break;
                case "Sick Day Management":
                    if(showSickDay){
                        displaySickDay = "<span class='blue'>Suggested Topic</span><br/>" + displaySickDay;
                        maxTopics++;
                    }
                    break;
                case "Nutrition":
                    if(showNutrition){
                            displayNutrition = "<span class='blue'>Suggested Topic</span><br/>" + displayNutrition; 
                            maxTopics++;
                    }
                    break;
                case "Vaccinations":
                    if(showVaccine){
                        displayVaccine = "<span class='blue'>Suggested Topic</span><br/>" + displayVaccine; 
                        maxTopics++;
                    }
                    break;
                case "Injections":
                    if(showInjection){
                        displayInjection = "<span class='blue'>Suggested Topic</span><br/>" + displayInjection;
                        maxTopics++;
                    }
                    break;
                case "Diabetes Targets and POCT":
                    if(showPOCT){
                            displayPOCT = "<span class='blue'>Suggested Topic</span><br/>" + displayPOCT;
                            maxTopics++;
                    }
                    break;
                default:
                    break;
        
                
            }
           
        }
            
        }
        
        
        if (suggested.length > 0){
            updatedData["pcp.suggestedFollowupTopics"] = suggested;
        } else {
            updatedData["pcp.suggestedFollowupTopics"] = undefined;
        
        } 
        
        }  else {
            displayHyper = "<span class='blue'>Suggested Topic</span><br/>" + displayHyper;
            if (showVaccine){displayVaccine = "<span class='blue'>Suggested Topic</span><br/>" + displayVaccine;}
            if (showNutrition){displayNutrition = "<span class='blue'>Suggested Topic</span><br/>" + displayNutrition;}
            updatedData["pcp.suggestedFollowupTopics"] = suggested;
        }
        
        //Bindings to control displayText
        updatedData["pcp.InjectionsTitleText"] = displayInjection;
        updatedData["pcp.VaccinationsTitleText"] = displayVaccine;
        updatedData["pcp.SickDayManagementTitleText"] = displaySickDay;
        updatedData["pcp.SelfMBGTitleText"] = displaySelf;
        updatedData["pcp.EyeHealthTitleText"] = displayEye;
        updatedData["pcp.FootcareTitleText"] = displayFoot;
        updatedData["pcp.HYPOGlycemiaTitleText"] = displayHypo;
        updatedData["pcp.HYPERGlycemiaTitleText"] = displayHyper;
        updatedData["pcp.DiabetesTargetsTitleText"] = displayPOCT;
        updatedData["pcp.NutritionTitleText"] = displayNutrition;
        
        //Bindings to control book now and rebook
        updatedData["pcp.HYPOGlycemiaBookNowDisplay"]=true;
        updatedData["pcp.FootcareBookNowDisplay"]=true;
        updatedData["pcp.EyeHealthBookNowDisplay"]=true;
        updatedData["pcp.SelfMBGBookNowDisplay"]=true;
        updatedData["pcp.HYPERGlycemiaBookNowDisplay"]=true;
        updatedData["pcp.SickDayManagementBookNowDisplay"]=true;
        updatedData["pcp.NutritionBookNowDisplay"]=true;
        updatedData["pcp.VaccinationsBookNowDisplay"]=true;
        updatedData["pcp.InjectionsBookNowDisplay"]=true;
        updatedData["pcp.DiabetesTargetsBookNowDisplay"]=true;

        updatedData["pcp.HYPOGlycemiaReBookDisplay"] = undefined;
        updatedData["pcp.FootcareReBookDisplay"] = undefined;
        updatedData["pcp.EyeHealthReBookDisplay"] = undefined;
        updatedData["pcp.SelfMBGReBookDisplay"] = undefined;
        updatedData["pcp.HYPERGlycemiaReBookDisplay"] = undefined;
        updatedData["pcp.SickDayManagementReBookDisplay"] = undefined;
        updatedData["pcp.NutritionReBookDisplay"] = undefined;
        updatedData["pcp.VaccinationsReBookDisplay"] = undefined;
        updatedData["pcp.InjectionsReBookDisplay"] = undefined;
        updatedData["pcp.DiabetesTargetsReBookDisplay"] = undefined;
    } 

    // Set panel time in GTM
    setPanelTime("Completeness Check",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    // check the HCP Notification PDF checkbox based on whether or not the optional field has a value
    const followUp = immutableData["ONMC.summary.followUp"];
    if(followUp){
        if(immutableData["ONMC.summary.followUp"].length > 0) {
            updatedData["ONMC.summary.followUpCheck"] = "Yes";
        }else{
            updatedData["ONMC.summary.followUpCheck"] = "No";
        } 
    }else{
        updatedData["ONMC.summary.followUpCheck"] = "No";
    }
    
    
    // Code for Referrals/Reasons
    
    // need two arrays for databindings and the designation values
    const designationValues = ["PHYSICIAN", "ENDOCRINOLOGIST", "REGISTERED NURSE", "PHARMACIST", "OTHER", "DIETITIAN"];
    const referralOptions = ["primaryCare", "endo", "nurse", "pharmacist", "otherRefer", "dietitian"];
    const referralReason1 = immutableData["ONMC.diabetes.referralReason1"] ? immutableData["ONMC.diabetes.referralReason1"] : "N/A";
    const referralReason2 = immutableData["ONMC.diabetes.referralReason2"] ? immutableData["ONMC.diabetes.referralReason2"] : "N/A";
    
    // run a loop that clears all reason databindings and checkbox databindings (this will take care of backtracking)
    for (let i=0; i<designationValues.length; i++) {
        updatedData["ONMC.diabetes."+ referralOptions[i]] = undefined;
        updatedData["ONMC.diabetes."+ referralOptions[i]+"Notes"] = undefined;
    }
    
    // check if the two designations are the same 
        // If they are the same AND the first follow-up question is answered YES, then run Loop A (involves concatenation) 
        // If different, run Loop B (no concatentation)
    
    if (immutableData["ONMC.followup.type1"] === immutableData["ONMC.followup.type2"] && immutableData["ONMC.followup.healthcareProviders"][0] === "Yes") {
        // Loop A - with concatenation 
        for (let i=0; i<designationValues.length; i++) {
                if (immutableData["ONMC.followup.type1"] === designationValues[i]) {
                updatedData["ONMC.diabetes."+ referralOptions[i]] = true;
                updatedData["ONMC.diabetes."+ referralOptions[i]+"Notes"] = "1: " + referralReason1 + " 2: " + referralReason2;
            }
        }
    } else {
        // Loop B - no concatentation 
        for (let i=0; i<designationValues.length; i++) {
            if (immutableData["ONMC.followup.type1"] === designationValues[i]) {
                updatedData["ONMC.diabetes."+ referralOptions[i]] = true;
                updatedData["ONMC.diabetes."+ referralOptions[i]+"Notes"] = referralReason1;
            }
        }
    
        for (let i=0; i<designationValues.length; i++) {
            if (immutableData["ONMC.followup.type2"] === designationValues[i]) {
                updatedData["ONMC.diabetes."+ referralOptions[i]] = true;
                updatedData["ONMC.diabetes."+ referralOptions[i]+"Notes"] = referralReason2;
            }
        }
    }

    if (designationValues.indexOf(immutableData["ONMC.followup.type1"]) == -1 && designationValues.indexOf(immutableData["ONMC.followup.type2"]) == -1) {
            
            updatedData["ONMC.diabetes.followUpDateField"] = undefined;
            updatedData["ONMC.diabetes.purpose"] = undefined;            
            }
            
    // Code for Checking the Follow-Up Date checkbox in the Diabetes Education PDF
    const followUpDateCheck = immutableData["ONMC.diabetes.followUpDateField"];
    if(followUpDateCheck){
        updatedData["ONMC.diabetes.followUpDate"] = "Yes";
    } else {
        updatedData["ONMC.diabetes.followUpDate"] = undefined;
    }
    
    // Databinding for "Copy of this record sent to" field on Diabetes Education Checklist (ONMC.diabetes.referralNames)
    // Databindings for Referral 1 and Referral 2 on Diabetes Take Home Summary Form (ONMC.diabetes.referral1, ONMC.diabetes.referral2)
    // backtracking if providers are added but then the user later chooses no
    const followUpName1 = immutableData["ONMC.followup.firstName1"] + " " + immutableData["ONMC.followup.lastName1"];
    const followUpName2 = immutableData["ONMC.followup.firstName2"] + " " + immutableData["ONMC.followup.lastName2"];
    const followUpHealthcareProviders = immutableData["ONMC.followup.healthcareProviders"] ? immutableData["ONMC.followup.healthcareProviders"][0] : undefined;
    const addAnotherProvider = immutableData["LOGIC.addAnotherProvider"] ? immutableData["LOGIC.addAnotherProvider"][0] : undefined;
    
    if (followUpHealthcareProviders === "Yes") {
        updatedData["ONMC.diabetes.referralNames"] = followUpName1;
        updatedData["ONMC.diabetes.referral1"] = followUpName1 + ", " + immutableData["ONMC.followup.type1"];
    } else if (followUpHealthcareProviders === "No") {
        updatedData["ONMC.diabetes.referralNames"] = undefined;
        updatedData["ONMC.diabetes.referral1"] = undefined;
        updatedData["ONMC.followup.firstName1"] = undefined;
        updatedData["ONMC.followup.lastName1"] = undefined;
        updatedData["ONMC.followup.type1"] = undefined;
        updatedData["ONMC.followup.telephoneNumber1"] = undefined;
        updatedData["ONMC.followup.email1"] = undefined;
        updatedData["ONMC.followup.fax1"] = undefined;
        updatedData["ONMC.diabetes.referralReason1"] = undefined;
    }
    
    if (addAnotherProvider === "yes" ) {
        updatedData["ONMC.diabetes.referralNames"] = followUpName1 + ", " + followUpName2;
        updatedData["ONMC.diabetes.referral2"] = followUpName2 + ", " + immutableData["ONMC.followup.type2"];
    } else if (addAnotherProvider === "no") {
        updatedData["ONMC.diabetes.referral2"] = undefined;
        updatedData["ONMC.followup.firstName2"] = undefined;
        updatedData["ONMC.followup.lastName2"] = undefined;
        updatedData["ONMC.followup.type2"] = undefined;
        updatedData["ONMC.followup.telephoneNumber2"] = undefined;
        updatedData["ONMC.followup.email2"] = undefined;
        updatedData["ONMC.followup.fax2"] = undefined;
        updatedData["ONMC.diabetes.referralReason2"] = undefined;
    }
    
    // Set panel time in GTM
    setPanelTime("Healthcare Provider Notification",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

