nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    console.log("Check1");
 if (immutableData["ONMC.summary.followUpValidation"] === true) {
     updatedData["ONMC.summary.followUpPrintout"] = immutableData["ONMC.summary.followUp"];
 } else {
     updatedData["ONMC.summary.followUpPrintout"] = undefined;
 }
 var materialsGivenComp = "";
 var summaryGoalsComp =  "";
 var summaryTextForNoTopicComp =  " ";
 var whatWillIDoComp = ""
 var readinwssNotesComp = "";
var notes2 = "";
 var advFlow = immutableData["ONMC.patient.followUpAdvanceFlow"] ? immutableData["ONMC.patient.followUpAdvanceFlow"][0] : undefined;
 var hasFollowUp = immutableData["ONMC.general.FollowUpTopicsGuide"] ? immutableData["ONMC.general.FollowUpTopicsGuide"] : "";
 if (advFlow === undefined){ advFlow = "No"}
 if (advFlow === "No") {
     hasFollowUp = "";
 } else {
        var materialsGivenComp = immutableData["ONMC.summary.resourcesCompiled"] ? immutableData["ONMC.summary.resourcesCompiled"]+ "<br />" : "";
        var summaryGoalsComp = immutableData["ONMC.summary.goalsCompiled"] ? immutableData["ONMC.summary.goalsCompiled"] + "<br />": "";
        var summaryTextForNoTopicComp = immutableData["ONMC.summary.InitialTextCompiled"] ? immutableData["ONMC.summary.InitialTextCompiled"] + "<br />": " ";
        var whatWillIDoComp = immutableData["ONMC.summary.whatIWillDoCompiled"] ? immutableData["ONMC.summary.whatIWillDoCompiled"] + "<br />": "";
        var readinwssNotesComp = immutableData["ONMC.general.readinessCompiled"] ? immutableData["ONMC.general.readinessCompiled"] + "<br />": "";
        var notes2 = immutableData["ONMC.summary.followUpCompiled"] ? immutableData["ONMC.summary.followUpCompiled"]:"" ;
    }


 if (immutableData["ONMC.summary.followUp"] || (advFlow === "Yes" && immutableData["ONMC.summary.followUpCompiled"])) {
     updatedData["ONMC.summary.followUpCheckFlag"] = "true";
 }
 var notes1 = immutableData["ONMC.summary.followUp"] ? immutableData["ONMC.summary.followUp"]:"" ;

 var formatedText1 = notes1.replace(/\n/g, "     ");
 var formatedText2 = notes2.replace(/\n/g, "     ");
 formatedText2 = formatedText2.length > 0 ? formatedText2 + "  " : formatedText2;

 updatedData["valForPDF"] = formatedText2 + formatedText1;



 var serviceType = immutableData["ONMC.service"] ? immutableData["ONMC.service"][0] : undefined;

 if (serviceType === "Diabetes") {
     var followupSelection = immutableData["followUpServiceType"] ? immutableData["followUpServiceType"][0] : undefined;
     var datedAssessmentRecorded = immutableData["todaysDate"];
     var interviewedBy = immutableData["user.fullName"];
     //Individualized BP Targets
     var bpTargets = immutableData["ONMC.training.bgTargetsNotes"] ? immutableData["ONMC.training.bgTargetsNotes"] : "";
     var patientsStageofreadiness = immutableData["ONMC.general.readiness"] ? immutableData["ONMC.general.readiness"] : " ";
     var patientsGoals = immutableData["ONMC.general.goals"] ? immutableData["ONMC.general.goals"] : " ";
     var howWillIGetThere = immutableData["ONMC.general.goals"] ? immutableData["ONMC.general.goals"] : " ";
     var resourcesUsed = immutableData["ONMC.general.resources"] ? immutableData["ONMC.general.resources"] : " ";
     var summaryTextForEyeFootPOCT = immutableData["ONMC.summary.OtherPleaseSpecify"] ? immutableData["ONMC.summary.OtherPleaseSpecify"] : undefined;
     var insulinType = immutableData["ONMC.specialtyTraining.insulinTypesNotes"] ? immutableData["ONMC.specialtyTraining.insulinTypesNotes"] : " ";
     //Biometrics
     //bp
     var systolic = immutableData["ONMC.general.bpSystolic"] ? immutableData["ONMC.general.bpSystolic"] : "";
     var diastolic = immutableData["ONMC.general.bpDiastolic"] ? immutableData["ONMC.general.bpDiastolic"] : "";
     //hb
     var hemo = immutableData["ONMC.general.hbNotes"] ? immutableData["ONMC.general.hbNotes"] : "";
     //fpg
     var fpg = immutableData["ONMC.general.fpgNotes"] ? immutableData["ONMC.general.fpgNotes"] : "";
     //tc
     var tgAmt = immutableData["ONMC.general.tg"] ? immutableData["ONMC.general.tg"] : "";
     var hdlAmt = immutableData["ONMC.general.hdl"] ? immutableData["ONMC.general.hdl"] : "";
     var ldlAmt = immutableData["ONMC.general.ldl"] ? immutableData["ONMC.general.ldl"] : "";
     var hdlRatio = immutableData["ONMC.general.hdlRatio"] ? immutableData["ONMC.general.hdlRatio"] : "";
     //pg

     //height
     var heightVal = immutableData["ONMC.general.height"] ? immutableData["ONMC.general.height"] : "";
     //weight
     var weightVal = immutableData["ONMC.general.weightNotes"] ? immutableData["ONMC.general.weightNotes"] : "";
     //List of resources
     var materialsGiven = immutableData["ONMC.summary.resources"] ? immutableData["ONMC.summary.resources"] : "";
     //Patient Goals
     var summaryGoals = immutableData["ONMC.summary.goals"] ? immutableData["ONMC.summary.goals"] : "";
     //Summary and goals
     var summaryTextForNoTopic = immutableData["ONMC.summary.InitialText"] ? immutableData["ONMC.summary.InitialText"] : " ";
     //patient - what will i do
     var whatWillIDo = immutableData["ONMC.summary.whatIWillDo"] ? immutableData["ONMC.summary.whatIWillDo"] : ""
     //Readiness for change
     var readinwssNotes = immutableData["ONMC.general.readiness"] ? immutableData["ONMC.general.readiness"] : "";
     //var physical activity
     var physicalActivity = immutableData["ONMC.lifestyle.exerciseActivityNotes"] ? immutableData["ONMC.lifestyle.exerciseActivityNotes"] : "";

     //Updated Regimen Fields 
     var AM = immutableData["ONMC.specialtyTraining.updatedRegimenAM"] ? "AM-" + immutableData["ONMC.specialtyTraining.updatedRegimenAM"] : " ";
     var hs = immutableData["ONMC.specialtyTraining.updatedRegimenHS"] ? "HS-" + immutableData["ONMC.specialtyTraining.updatedRegimenHS"] : " ";
     var noon = immutableData["ONMC.specialtyTraining.updatedRegimenNoon"] ? "Noon-" + immutableData["ONMC.specialtyTraining.updatedRegimenNoon"] : " ";
     var other = immutableData["ONMC.specialtyTraining.updatedRegimenOther"] ? "Other-" + immutableData["ONMC.specialtyTraining.updatedRegimenOther"] : " ";
     var supper = immutableData["ONMC.specialtyTraining.updatedRegimenSupper"] ? "Supper-" + immutableData["ONMC.specialtyTraining.updatedRegimenSupper"] : " ";
     var type1 = immutableData["ONMC.specialtyTraining.updatedRegimenType1"] ? "Type 1-" + immutableData["ONMC.specialtyTraining.updatedRegimenType1"] : " ";
     var type2 = immutableData["ONMC.specialtyTraining.updatedRegimenType2"] ? "Type 2-" + immutableData["ONMC.specialtyTraining.updatedRegimenType2"] : " ";


     var serviceNewOrCon = immutableData["service.continuedOrNew"] ? immutableData["service.continuedOrNew"][0] : undefined;
     
     if (serviceNewOrCon === "New Service") {
         var finalFormat = "<b>Medication Review Date: </b>" + datedAssessmentRecorded +
             "<br/><b>Interview Conducted By: </b> " + interviewedBy +
             "<br/><b>Biometrics:  </b>" +
             "<br/>\t  *" + " HbA1c:" + hemo +
             "<br/>\t  * BP (today`s average): Systolic:" + systolic + ", Diastolic: " + diastolic +
             "<br/>\t  * FPG: " + fpg +
             "<br/>\t  * TC: TG-" + tgAmt + ", HDL-" + hdlAmt + ", LDL-" + ldlAmt + ", HDL Ratio-" + hdlRatio +
             "<br/>\t  * Physical Activity: " + physicalActivity +
             "<br/>\t  * Weight: " + weightVal +
             "<br/>\t  * Height: " + heightVal +
             "<br/><b>Insulin Types: </b>" + insulinType +
             "<br/><b>Updated Insulin Regimen: </b>" + type1 + ", " + AM + ", " + noon + ", " + supper + ", " + type2 + ", " + hs + ", " + other +
             "<br/><b>Patient`s stage of Readiness for Change: </b>" + readinwssNotesComp + readinwssNotes +
             "<br/><b>Patient Goals: </b>" + summaryGoalsComp + summaryGoals +
             "<br/><b>What patient will do to reach goals: </b>" + whatWillIDoComp + whatWillIDo +
             "<br/><b>List of resources provided: </b>" + materialsGivenComp + materialsGiven +
             "<br/><b>Summary of Discussion: </b>" + summaryTextForNoTopicComp + summaryTextForNoTopic;
     } else {
         var finalFormat = "<b>Medication Review Date: </b>" + datedAssessmentRecorded +
             "<br/><b>Follow-up Topic: </b> " + "No topic selected" +
             "<br/><b>Interview Conducted By: </b> " + interviewedBy +
             "<br/><b>Biometrics:  </b>" +
             "<br/>\t  *" + " HbA1c:" + hemo +
             "<br/>\t  * BP (today`s average): Systolic:" + systolic + ", Diastolic: " + diastolic +
             "<br/>\t  * FPG: " + fpg +
             "<br/>\t  * TC: TG-" + tgAmt + ", HDL-" + hdlAmt + ", LDL-" + ldlAmt + ", HDL Ratio-" + hdlRatio +
             "<br/>\t  * Physical Activity: " + physicalActivity +
             "<br/>\t  * Weight: " + weightVal +
             "<br/>\t  * Height: " + heightVal +
             "<br/><b>Insulin Types: </b>" + insulinType +
             "<br/><b>Updated Insulin Regimen: </b>" + type1 + ", " + AM + ", " + noon + ", " + supper + ", " + type2 + ", " + hs + ", " + other +
             "<br/><b>Patient`s stage of Readiness for Change: </b>" + readinwssNotes +
             "<br/><b>Patient Goals: </b>" + summaryGoals +
             "<br/><b>What patient will do to reach goals: </b>" + whatWillIDo +
             "<br/><b>List of resources provided: </b>" + materialsGiven +
             "<br/><b>Summary of Discussion: </b>" +summaryTextForNoTopic;
     }


     var PrevData = immutableData["DAPNotesSummaryPreviousSession"];
     if (PrevData !== undefined) {
         var text = immutableData["DAPNotesSummaryPreviousSession"];
         updatedData["DAPNotesSummary"] = text + "<br/><hr/><br/>" + finalFormat;
     } else {
        //updatedData["DAPNotesSummary"] = hasFollowUp + finalFormat;
         updatedData["DAPNotesSummary"] =  finalFormat;
     }
 }
 
 
 //This JS is added to summary and goals to trigger the A1C Recommended flags + Build current session HTML Block
//[Needs[0] convert to .lower to account for yes or Yes, A1C Check box ,A1C VALUE - CHeck to see length > 0 ]
function calDays(days, action, startDate){
    var today = startDate ? new Date(startDate) : new Date();
    console.log("Date inside of CalDays function\t" + today )
    if (action === "add"){ today.setDate(today.getDate() + days); }
console.log(
    "Future\t" + today)
    var dd = today.getDate();
    var mm = today.getMonth() + 1 ;
    var yyyy = today.getFullYear();

    if(dd<10){dd = '0' + dd;}
    if(mm<10){mm='0' + mm; }
    return   yyyy + '-'  +  mm + '-'  + dd  ;
}
var haspcp = immutableData["enableFormPCPFlow"] ? immutableData["enableFormPCPFlow"] : false;
if (serviceNewOrCon === "Continue Service" && haspcp ) {


    var topicAbbreviations = {
        "HYPOGlycemia": ["hasHealthTabHypo", "hasHealthTabA1CCheckHypo", "hasHealthTabA1CHypo"],
        "Foot Care": ["hasHealthTabfootcare", "hasHealthTabA1CCheckfootcare", "hasHealthTabA1Cfootcare"],
        "Eye Health": ["hasHealthTabEyeHealth", "hasHealthTabA1CCheckEyeHealth", "hasHealthTabA1CEyeHealth"],
        "Self-monitoring of Blood Glucose": ["hasHealthTabSMBG", "hasHealthTabA1CCheckSMBG", "hasHealthTabA1CSMBG"],
        "HYPERGlycemia": ["hasHealthTabHyper", "hasHealthTabA1CCheckHyper", "hasHealthTabA1CHyper"],
        "Sick Day Management": ["hasHealthTabSiDM", "hasHealthTabA1CCheckSiDM", "hasHealthTabA1CSiDM"],
        "Nutrition": ["hasHealthTabNutrition", "hasHealthTabA1CCheckNutrition", "hasHealthTabA1CNutrition"],
        "Vaccinations": ["hasHealthTabVaccine", "hasHealthTabA1CCheckVaccine", "hasHealthTabA1CVaccine"],
        "Injections": ["hasHealthTabInjSite", "hasHealthTabA1CCheckInjSite", "hasHealthTabA1CInjSite"],
        "Diabetes Targets and POCT":["poctSourceLab","poctSourceLabValueCheck","poctSourceLabValueNum","poctSourceLabDateCheck","poctSourceLabDate","poctSourceHealthTab","poctSourceHealthTabValueCheck","poctSourceHealthTabValueNum","poctSourceHealthTabDateCheck","poctSourceHealthTabDate"],
        "Diabetes": ["ONMC.general.hbAmountDate"]
    }
    var today = getTodayDate();
    var bookingStatus = "";
    var dateRecommended = calDays(0, 'add');
    var testRan = false;
    var followupTopic = immutableData["followUpServiceType"] ? immutableData["followUpServiceType"][0] : "Diabetes";
    if(followupTopic === undefined){followupTopic = "Diabetes"}
    if (followupTopic === "Diabetes") {
        var runGeneric = true;
        var followUpDate = immutableData["ONMC.general.hbAmountDate"] ? immutableData["ONMC.general.hbAmountDate"] : undefined;
        var valueAC = immutableData["ONMC.general.hbAmount"] ? immutableData["ONMC.general.hbAmount"] : undefined;
         var acResults = undefined;
        if (followUpDate !== undefined && valueAC !== undefined) {
            testRan = true;
            acResults = {"dateDelivered": followUpDate, "testResult":valueAC}
            var device = immutableData["ONMC.general.hbAmountDevice"] ? immutableData["ONMC.general.hbAmountDevice"] : false;
            console.log("Device\n"+device);
            var days90 = calDays(91, 'add', followUpDate);
            if (new Date(days90) > new Date(today)) {
                dateRecommended = days90;   
            } 
            updatedData["pcp.A1CRecommendedDate"] = dateRecommended;
            if(device) {runGeneric = false}
            console.log("before running")
           
        } 
        console.log("gnertic value \n" + runGeneric )
        if (runGeneric) {
            var pcpObjectCurrent = immutableData["personalizedCarePlanObject"] ? immutableData["personalizedCarePlanObject"]["A1CObjectActive"] : [];
            if (pcpObjectCurrent.length > 0) {
                var activeIndex = pcpObjectCurrent.findIndex(obj => obj.topic === "A1C")
                if (activeIndex !== -1) {
                   bookingStatus = pcpObjectCurrent[activeIndex]["status"];
                    var notes = "";
                    var newUpdates ="";
                    if(testRan === true){
                        console.log("Updating Recommended date for test ran ")
                        newUpdates =  '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/>';                   
                   }else {
                    console.log("Using default recommended date from last session")
                    dateRecommended = pcpObjectCurrent[activeIndex]["dateRecommended"] ? pcpObjectCurrent[activeIndex]["dateRecommended"] : "";
                    newUpdates = '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/>';
                    updatedData["pcp.A1CRecommendedDate"] = dateRecommended;
                   }
    
                    if (bookingStatus == "Later") {
                        notes = '<h3>A1C <span class="redBlock"> Added to Plan - Booking Pending</span></h3>'
                    } else if (bookingStatus === "Book") {
                        notes = '<h3>A1C <span class="redBlock"> Delivery Pending - ' + pcpObjectCurrent[activeIndex]["dateBooked"]; + '</span></h3>'
                    }
                    updatedData["pcp.A1CBookNowDisplay"] = undefined;
                    updatedData["pcp.A1CReBookDisplay"] = true;
                    
                    updatedData["pcp.A1CTitleText"] = newUpdates + notes ;
                } else {
                        if(testRan){
                            var notes =  '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/>';                      
                       } else {
                        var notes = immutableData["form_0"]["pcp.A1CRecommendedDate"] ?  '<span class="blue">Suggested For ' + immutableData["form_0"]["pcp.A1CRecommendedDate"] + '</span> <br/>' : "";
                        updatedData["pcp.A1CRecommendedDate"] = immutableData["form_0"]["pcp.A1CRecommendedDate"];
                    
                        }
                       updatedData["pcp.A1CBookNowDisplay"] = true;
                        updatedData["pcp.A1CReBookDisplay"] = undefined;
                        updatedData["pcp.A1CTitleText"] = notes + '<h3>A1C</h3>';
                }
    
    
            } else {
                if(testRan){
                    var notes = '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/>'
               }else {
                var notes = immutableData["form_0"]["pcp.A1CRecommendedDate"] ?  '<span class="blue">Suggested For ' + immutableData["form_0"]["pcp.A1CRecommendedDate"] + '</span> <br/>' : "";
                updatedData["pcp.A1CRecommendedDate"] = immutableData["form_0"]["pcp.A1CRecommendedDate"];
            }
                        //var notes = immutableData["form_0"]["pcp.A1CRecommendedDate"] ?  '<span class="blue">Suggested For ' + immutableData["form_0"]["pcp.A1CRecommendedDate"] + '</span> <br/>' : "";
                        updatedData["pcp.A1CBookNowDisplay"] = true;
                        updatedData["pcp.A1CReBookDisplay"] = undefined;
                        updatedData["pcp.A1CTitleText"] = notes + '<h3>A1C</h3>';
            }
            updatedData["personalizedCarePlanHTML.a1cFollowUp"] = configurePCPHtmlBlock(immutableData["personalizedCarePlanObject"]["A1CObjectActive"], immutableData["personalizedCarePlanObject"]["A1CObjectCompleted"], false)
            updatedData["personalizedCarePlanHTML.acResultsCurrent"] = configureACResults(immutableData["personalizedCarePlanObject"]["A1CResults"],acResults)
           
        }else {
            updatedData["pcp.A1CBookNowDisplay"] = true;
            updatedData["pcp.A1CReBookDisplay"] = undefined;
            updatedData["personalizedCarePlanHTML.acResultsCurrent"] = configureACResults(immutableData["personalizedCarePlanObject"]["A1CResults"],acResults)
            
            updatedData["pcp.A1CTitleText"] = '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/><h3>A1C</h3>';
            updatedData["personalizedCarePlanHTML.a1cFollowUp"] = configurePCPHtmlBlock(immutableData["personalizedCarePlanObject"]["A1CObjectActive"], immutableData["personalizedCarePlanObject"]["A1CObjectCompleted"],true,followUpDate)
       
        }
    }else  if (followupTopic === "Diabetes Targets and POCT") {
        var values = topicAbbreviations[ "Diabetes Targets and POCT"];
        var haslab = immutableData[values[0]] ? immutableData[values[0]] : false;
        var haslabValue = immutableData[values[1]] ? immutableData[values[1]] : false;
        var labValue = immutableData[values[2]] ? immutableData[values[2]] : undefined;
        var hasLabDate = immutableData[values[3]] ? immutableData[values[3]] : false;
        var labDate = immutableData[values[4]] ? immutableData[values[4]] : undefined;
        var hasHealth = immutableData[values[5]] ? immutableData[values[5]] : false;
        var hasHealthValue = immutableData[values[6]] ? immutableData[values[6]] : false;
        var healthValue = immutableData[values[7]] ? immutableData[values[7]] : undefined;
        var hasHealthDate = immutableData[values[8]] ? immutableData[values[8]] : false;
        var healthDate = immutableData[values[9]] ? immutableData[values[9]] : undefined;
        if((haslab && hasLabDate && haslabValue && labValue !== undefined && labDate !== undefined) || (hasHealth && hasHealthDate && hasHealthValue && labValue !== undefined && labDate !== undefined)){
            var dateForRecommended = new Date();
            if(isNaN(Date.parse(labDate))){
                dateForRecommended = healthDate;
            }else if (isNaN(Date.parse(healthDate))){
                dateForRecommended = labDate ;
            } else {
                dateForRecommended = new Date(Math.max(Date.parse(labDate),Date.parse(healthDate)))
    
            }
            dateRecommended = calDays(91, 'add',dateForRecommended);
            if (new Date(days90) > new Date(today)) {
                dateRecommended = days90;
            }
            updatedData["pcp.A1CBookNowDisplay"] = true;
            updatedData["pcp.A1CReBookDisplay"] = undefined;
            updatedData["pcp.A1CRecommendedDate"] = dateRecommended;
            updatedData["pcp.A1CTitleText"] = '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/><h3>A1C</h3>';
            updatedData["personalizedCarePlanHTML.a1cFollowUp"] = configurePCPHtmlBlock(immutableData["personalizedCarePlanObject"]["A1CObjectActive"], immutableData["personalizedCarePlanObject"]["A1CObjectCompleted"], true, followUpDate)
        
    
        } else {
            var pcpObjectCurrent = immutableData["personalizedCarePlanObject"] ? immutableData["personalizedCarePlanObject"]["A1CObjectActive"] : [];
                if (pcpObjectCurrent.length > 0) {
                    var activeIndex = pcpObjectCurrent.findIndex(obj => obj.topic === "A1C")
                    if (activeIndex !== -1) {
                        dateRecommended = pcpObjectCurrent[activeIndex]["dateRecommended"] ? pcpObjectCurrent[activeIndex]["dateRecommended"] : "";
                        bookingStatus = pcpObjectCurrent[activeIndex]["status"];
                        var notes = "";
                        if (bookingStatus == "Later") {
                            notes = '<span class="blue">Suggested For ' + dateRecommended + '</span> <br/><h3>A1C <span class="redBlock"> Added to Plan - Booking Pending</span></h3>'
                        } else if (bookingStatus === "Book") {
                            notes = '<h3>A1C <span class="redBlock"> Delivery Pending - ' + pcpObjectCurrent[activeIndex]["dateBooked"]; + '</span></h3>'
                        }
                        updatedData["pcp.A1CBookNowDisplay"] = undefined;
                        updatedData["pcp.A1CReBookDisplay"] = true;
                        updatedData["pcp.A1CRecommendedDate"] = dateRecommended;
                        updatedData["pcp.A1CTitleText"] = notes ;
                    } else {
                        updatedData["pcp.A1CBookNowDisplay"] = true;
