        updatedData["ONMC.lifestyle.tobaccoNumberPDF"]=undefined;
       updatedData["ONMC.lifestyle.smokingCessationCheck"] = undefined;
       updatedData["ONMC.lifestyle.tobaccoCessationOffered"] = undefined;
       updatedData["ONMC.lifestyle.smokingNotes"] = undefined ;
       updatedData["ONMC.lifestyle.tobaccoCessationOfferedNotes"] = undefined;
      //  updatedData["ONMC.lifestyle.tobacco"]=true;
        updatedData["ONMC.lifestyle.tobaccoNo"]=true;
        updatedData["ONMC.lifestyle.tobaccoPreviousText"]=undefined;
        updatedData["ONMC.lifestyle.tobaccoYes"]=false;
         updatedData["ONMC.lifestyle.hasSmoking"]=false;
         updatedData["ONMC.lifestyle.smokingNotes"]=undefined;
    }

    //Backtracking for recreational drugs 
    if(recDrugsChoice === "No"){
        updatedData["ONMC.lifestyle.recDrugsFrequency"] = undefined;
    }
    
    //Backtracking for alcohol
    if(alcoholChoice === "No"){
        updatedData["ONMC.lifestyle.alcoholFrequency"] = undefined;
    }
    
    
    //Function that checks if there is a length in a field, and replaces the 'check' binding with 'yes'
    function checkForLength(immutableData, updatedData, binding, checkBinding) {
        const field = immutableData[binding] ? immutableData[binding] : null;
        let checked = "no";
        if(field !== null){
            if(field.replace(/\s/g, '').length > 0){
                checked = "yes";
            }
        }
        updatedData[checkBinding] = checked;
    }
    
    //Running the function for fields that require a check if there is contents in the text field
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.smokingCessation", "ONMC.lifestyle.smokingCessationCheck");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.exerciseRegimen", "ONMC.lifestyle.exerciseRegimenCheck");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.exerciseActivityNotes", "ONMC.lifestyle.exerciseActivity");
    checkForLength(immutableData, updatedData, "ONMC.general.lifestyleDietNotes", "ONMC.general.lifestyleDiet");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.dietaryConcernsNotes", "ONMC.lifestyle.dietaryConcerns");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.tobaccoCessationOfferedNotes", "ONMC.lifestyle.tobaccoCessationOffered");
    
    // Backtracking for if patientDietCheck (form checkbox) is false, then patientDiet, patientDietNotes, patientConcerns, patientConcernNotes all need to be undefined
    if(immutableData["ONMC.lifestyle.patientDietCheck"] === false) {
        updatedData["ONMC.general.lifestyleDietNotes"] = undefined;
        updatedData["ONMC.general.lifestyleDiet"] = undefined;
        updatedData["ONMC.lifestyle.dietaryConcerns"] = undefined;
        updatedData["ONMC.lifestyle.dietaryConcernsNotes"] = undefined;
    }
    
    // Backtracking for if exerciseOverallCheck (form checkbox) is false, exercise, exerciseCheck, physical, phsyicalCheck undefined as well 
    if(immutableData["ONMC.lifestyle.exerciseOverallCheck"] === false) {
        updatedData["ONMC.lifestyle.exerciseRegimen"] = undefined;
        updatedData["ONMC.lifestyle.exerciseRegimenCheck"] = undefined;
        updatedData["ONMC.lifestyle.exerciseActivity"] = undefined;
        updatedData["ONMC.lifestyle.exerciseActivityNotes"] = undefined;
    }
    
    // Generates a 'yes' for the lifestyle checkbox in diabetes form if there is any 1 of 3 lifestyle questions 
    
    if (immutableData["ONMC.lifestyle.exerciseRegimenCheck"] === "yes" || immutableData["ONMC.general.lifestyleDiet"] === "yes" || immutableData["ONMC.general.lifestyleStress"] === true ) {
        updatedData["ONMC.general.lifestyleCheck"] = "yes";
    } else {
        updatedData["ONMC.general.lifestyleCheck"] = undefined;
    }
    
    // backtracking for stress, other, and notes
    if(immutableData["ONMC.general.lifestyleStress"] === false){
        updatedData["ONMC.general.lifestyleStressNotes"] = undefined;
    }
    
    if(immutableData["ONMC.lifestyle.otherCheck"] === false){
        updatedData["ONMC.lifestyle.other"] = undefined;
    }
    
    if(immutableData["ONMC.lifestyle.notesCheck"] === false){
        updatedData["ONMC.lifestyle.notes"] = undefined;
    }

    // Alcohol + Recreational Drugs concatenation for diabetes form + backtracking
    
    if (alcoholChoice === "Yes" && recDrugsChoice === "Yes"){
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = "yes";
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = "A: " + immutableData["ONMC.lifestyle.alcoholFrequency"] + " RD: " + immutableData["ONMC.lifestyle.recDrugsFrequency"];
    }else if (alcoholChoice === "Yes" && recDrugsChoice === "No") {
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = "yes";
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = "A: " + immutableData["ONMC.lifestyle.alcoholFrequency"];
    } else if (alcoholChoice === "No" && recDrugsChoice === "Yes") {
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = "yes";
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = "RD: " + immutableData["ONMC.lifestyle.recDrugsFrequency"];
    } else {
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = undefined;
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = undefined; 
    }
    
    // Set panel time in GTM
    setPanelTime("Lifestyle Information",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

