nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    // concatenated fields
    var dateA1C = immutableData["ONMC.general.hbAmountDate"] ? " ,Date: " + immutableData["ONMC.general.hbAmountDate"] : "";
    var device = immutableData["ONMC.general.hbAmountDevice"] ?", Device: Abbott AfinionTM 2 analyzer / Point of care test (POCT)" : "";
    var a1cAdditional = "";
    var advFlow = immutableData["ONMC.patient.followUpAdvanceFlow"] ? immutableData["ONMC.patient.followUpAdvanceFlow"][0] : "No";
    if (advFlow !== "Yes"){
        a1cAdditional = dateA1C + device ; 
    } else {
        updatedData["ONMC.general.hbAmountDevice"] = undefined;
    }
    updatedData["ONMC.general.hbNotes"] = immutableData["ONMC.general.hbAmount"] ? immutableData["ONMC.general.hbAmount"] + " %" + a1cAdditional : undefined;
 
    updatedData["ONMC.general.fpgNotes"] = immutableData["ONMC.general.fpgAmount"] ? immutableData["ONMC.general.fpgAmount"] + " mmol/L" : undefined;
    updatedData["ONMC.general.ldl"] = immutableData["ONMC.general.ldlAmount"] ? immutableData["ONMC.general.ldlAmount"] + " mmol/L" : undefined;
    updatedData["ONMC.general.hdl"] = immutableData["ONMC.general.hdlAmount"] ? immutableData["ONMC.general.hdlAmount"] + " mmol/L" : undefined;
    updatedData["ONMC.general.tg"] = immutableData["ONMC.general.tgAmount"] ? immutableData["ONMC.general.tgAmount"] + " mmol/L" : undefined;
    updatedData["ONMC.general.weightNotes"] = immutableData["ONMC.general.weightAmount"] ? immutableData["ONMC.general.weightAmount"] + " " + immutableData["ONMC.general.weightUnit"] : undefined;
    updatedData["ONMC.general.height"] = immutableData["ONMC.general.heightAmount"] ? immutableData["ONMC.general.heightAmount"] + " " + immutableData["ONMC.general.heightUnit"] : undefined;
    updatedData["ONMC.general.waist"] = immutableData["ONMC.general.waistAmount"] ? immutableData["ONMC.general.waistAmount"] + " " + immutableData["ONMC.general.waistUnit"] : undefined;
    
    // backtracking
    //const checkboxes = ["hb", "fpg", "cognitive", "medcheckAvailable", "dtp"];
    
    const checkboxes = ["cognitive", "medcheckAvailable", "dtp"];
    for(let i=0; i<checkboxes.length; i++){
        const binding = immutableData["ONMC.general."+ checkboxes[i]] ? immutableData["ONMC.general."+ checkboxes[i]][0] : undefined;
        if (binding !== 'Yes') {
            updatedData["ONMC.general."+ checkboxes[i]] = undefined;
            updatedData["ONMC.general."+ checkboxes[i]+"Notes"] = undefined;
            updatedData["ONMC.general."+ checkboxes[i]+"Amount"] = undefined;
        }
    }
    var onmcFields = {
    "ONMC.general.fpg": ["ONMC.general.fpgAmount"],
    "ONMC.general.weight": ["ONMC.general.weightAmount", "ONMC.general.heightAmount", "ONMC.general.waistAmount", "ONMC.general.bmi"],
    "ONMC.general.bp": ["ONMC.general.bpSystolic", "ONMC.general.bpDiastolic"],
    "ONMC.general.tc ": ["ONMC.general.hdlRatio","ONMC.general.ldlAmount", "ONMC.general.hdlAmount","ONMC.general.tgAmount", "ONMC.general.tcNotesPOCT" ],
    "ONMC.general.hb": ["ONMC.general.hbAmount","ONMC.general.hbNotesPOCTCompiled", "ONMC.general.hbNotesPOCT" ] //,"ONMC.general.hbAmountDate" , "ONMC.general.hbAmountDevice" ]
}
for (var field in onmcFields){
    var setOn = undefined;
    console.log(field)
    for (var x = 0 ; x < onmcFields[field].length ; x++){
        var fieldUsed = immutableData[onmcFields[field][x]] ? immutableData[onmcFields[field][x]] : "";
        fieldUsed = fieldUsed.toString();
        console.log("Binding: \t" + onmcFields[field][x])
        console.log(fieldUsed + "\t text field at")
        console.log("length: \t" + fieldUsed.length )
        if (fieldUsed.length > 0  ){
            setOn = ["Yes"];
        }
    }
    updatedData[field] = setOn ; 
}
   
   
    
    // Set panel time in GTM
    setPanelTime("Diabetes General Information & Health - General Information",immutableData);
    var insulinUsedChecked = immutableData["ONMC.general.insulinUse"] ? immutableData["ONMC.general.insulinUse"][0] : undefined;
if(insulinUsedChecked === undefined){
    updatedData["ONMC.general.insulinUseNo"]=false;
    updatedData["ONMC.general.insulinUseYes"]=false;
}else if (insulinUsedChecked === "No"){
    updatedData["ONMC.general.insulinUseNo"]=true;
      updatedData["ONMC.general.insulinUseYes"]=false;
 
}else if (insulinUsedChecked === "Yes"){
    updatedData["ONMC.general.insulinUseNo"]=false;
      updatedData["ONMC.general.insulinUseYes"]=true;
   
}

  var hypoG = immutableData["ONMC.general.hypoG"] ? immutableData["ONMC.general.hypoG"][0] : undefined;
if(hypoG === undefined){
    updatedData["ONMC.general.hypoGNo"]=false;
    updatedData["ONMC.general.hypoGYes"]=false;
}else if (hypoG === "No"){
    updatedData["ONMC.general.hypoGNo"]=true;
      updatedData["ONMC.general.hypoGYes"]=false;
 
}else if (hypoG === "Yes"){
    updatedData["ONMC.general.hypoGNo"]=false;
        updatedData["ONMC.general.hypoGYes"]=true;
   
}

  var bpS= immutableData["ONMC.general.bpSystolic"];
  var bpD = immutableData["ONMC.general.bpDiastolic"];
  if (bpS !== undefined || bpD !== undefined){
      updatedData["ONMC.general.bpYes"] = true;
      if(bpS === undefined){
          bpS="BP Systolic value hasn`t been entered";
      }
      if(bpD === undefined){
          bpD="BP Diastolic value hasn`t been entered";
      }
      var bpNotes = "Systolic: " + bpS +", "+ "Diastolic: " + bpD;
      updatedData["ONMC.general.bpNotes"]= bpNotes;
  } else {
        updatedData["ONMC.general.bpYes"] = false;
         updatedData["ONMC.general.bpNotes"]=undefined;
  }

    //Your Custom Code Ends Here
    callback(updatedData);
}

