function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    // backtracking
    const checkboxes = ["medManagement", "footCare", "dn", "footCondition", "shoeFit", "bpMonitoring", "cv", "mentalHealthAssess", "sexualHealth", "eyeHealth", "dentalHygiene", "drivingGuidelines", "diabetesTravel", "influenza", "pneumococcal", "otherImmunizations"];
    for(let i=0; i<checkboxes.length; i++){
        const binding = immutableData["ONMC.general."+ checkboxes[i]] ? immutableData["ONMC.general."+ checkboxes[i]][0] : undefined;
        if(binding !== 'Yes'){
            updatedData["ONMC.general."+ checkboxes[i]] = undefined;
            updatedData["ONMC.general."+ checkboxes[i]+"Notes"] = undefined;
        }
    }
    
    // immunizations has multiple child multichecks and notes, so set this separately
    const immunizations = immutableData["ONMC.general.immunizations"] ? immutableData["ONMC.general.immunizations"][0] : undefined;
    if(immunizations !== 'Yes'){
        updatedData["ONMC.general.influenza"] = undefined;
        updatedData["ONMC.general.influenzaNotes"] = undefined;
        updatedData["ONMC.general.pneumococcal"] = undefined;
        updatedData["ONMC.general.pneumococcalNotes"] = undefined;
        updatedData["ONMC.general.otherImmunizations"] = undefined;
        updatedData["ONMC.general.otherImmunizationsNotes"] = undefined;
    }
    
    // Set panel time in GTM
    setPanelTime("Diabetes General Information & Health - Health and Wellness Information",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

