function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    // backtracking
    const checkboxes = ["insulinTypes", "penHandling", "sitePrep", "injectionTraining", "properInjection", "patientDemo", "dosageAdj", "missedDoses", "carbCount", "emergencyMgmt", "substance", "other", "otherComps", "passport", "sickDays", "afterHours", "directory", "otherMaterial"];
    for(let i=0; i<checkboxes.length; i++){
        const binding = immutableData["ONMC.specialtyTraining."+ checkboxes[i]] ? immutableData["ONMC.specialtyTraining."+ checkboxes[i]][0] : undefined;
        if(binding !== 'Yes'){
            updatedData["ONMC.specialtyTraining."+ checkboxes[i]] = undefined;
            updatedData["ONMC.specialtyTraining."+ checkboxes[i]+"Notes"] = undefined;
        }
    }
    
    // updatedInsulinRegimen has multiple child texts, so set this separately
    const updatedInsulinRegimen = immutableData["ONMC.specialtyTraining.updatedRegimen"] ? immutableData["ONMC.specialtyTraining.updatedRegimen"][0] : undefined;
    if(updatedInsulinRegimen !== 'Yes'){
        updatedData["ONMC.specialtyTraining.updatedRegimen"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenType1"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenAM"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenNoon"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenSupper"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenType2"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenHS"] = undefined;
        updatedData["ONMC.specialtyTraining.updatedRegimenOther"] = undefined;
    }
    
    // Push panel time to GTM
    setPanelTime("Diabetes Training - Specialty Training Resources ",immutableData)
 var otherMaterials = immutableData["ONMC.specialtyTraining.otherMaterial"] ? immutableData["ONMC.specialtyTraining.otherMaterial"][0] : undefined
 if(otherMaterials !== undefined){
     updatedData["ONMC.specialtyTraining.otherMaterialckBox"]=true;
 } else {
          updatedData["ONMC.specialtyTraining.otherMaterialckBox"]=false;
 }
 
 var x ;
 var textForReadiness="";
var hasLink = false;
var serviceNew = immutableData["service.continuedOrNew"] ? immutableData["service.continuedOrNew"][0] : undefined;
if(serviceNew !== "Continue Service"){
    
    var links = {
        "managingBPCheckbox":"Managing Blood Sugar: https://guidelines.diabetes.ca/docs/patient-resources/managing-your-blood-sugar.pdf \n",
        "abcdLinkCheckbox":"ABCDESSS of Staying Healthy with Diabetes: https://guidelines.diabetes.ca/reduce-complications/patient-abcdes \n",
        "highBPLinkCheckbox":"High Blood Pressure and Diabetes: https://guidelines.diabetes.ca/docs/patient-resources/high-blood-pressure-and-diabetes.pdf \n",
        "vascularLinkCheckbox":"Vascular Self Assessment Tool: https://guidelines.diabetes.ca/docs/patient-resources/medications-for-protection-from-heart-disease-and-stroke.pdf \n",
        "cholLinkCheckbox":"Cholesterol and Diabetes: https://guidelines.diabetes.ca/docs/patient-resources/cholesterol-and-diabetes.pdf \n",
        "bgLogLinkCheckbox":"Blood Glucose Log: https://guidelines.diabetes.ca/docs/patient-resources/blood-glucose-log.pdf \n",
        "bgLinkCheckbox":"Lows and Highs: Blood Glucose Levels: https://guidelines.diabetes.ca/docs/patient-resources/lows-and-highs-blood-sugar-levels.pdf \n",
        "hypoLinkCheckbox":"HYPOglycemia: Low Blood Sugar in Adults: https://guidelines.diabetes.ca/docs/patient-resources/hypoglycemia-low-blood-sugar-in-adults.pdf \n",
        "healthyEatingLinkCheckbox":"Just the Basics: Tips for Healthy Eating: https://guidelines.diabetes.ca/docs/patient-resources/just-the-basics-EN.pdf \n",
        "handyPortionLinkCheckbox":"Handy Portion Guide: https://guidelines.diabetes.ca/docs/patient-resources/handy-portion-guide.pdf \n",
        "glyIndLinkCheckbox":"The Glycemic Index: https://guidelines.diabetes.ca/docs/patient-resources/glycemic-index-food-guide.pdf \n",
        "stayingSafeLinkCheckbox":"Staying Safe When You Have Diabetes and Are Sick or At Risk of Dehydration: https://guidelines.diabetes.ca/docs/patient-resources/stay-safe-when-you-have-diabetes-and-sick-or-at-risk-of-dehydration.pdf \n",
        "openEyesLinkCheckbox":"Open Your Eyes to an Eye Exam: https://guidelines.diabetes.ca/docs/patient-resources/open-your-eyes-to-an-eye-exam.pdf \n",
        "goodHealthLinkCheckbox":"Foot Care: A Step Toward Good Health: https://guidelines.diabetes.ca/docs/patient-resources/foot-care.pdf \n",
        "shoeFitLinkCheckbox":"Finding the Proper Shoe Fit: https://guidelines.diabetes.ca/docs/patient-resources/finding-the-proper-shoe-fit.pdf \n",
        "doAnddDontsLinkCheckbox":"Diabetes and Foot care: A Patient’s List – Do’s and Don’ts: https://guidelines.diabetes.ca/docs/patient-resources/diabetes-and-footcare-a-patient-checklist.pdf \n",
        "injTechniqueLinkCheckbox":"Injection Technique: http://fit4diabetes.com/files/3615/8833/4448/Patient_BD4840_FIT_TechPlus_TechForAll_A4_2019_STG07.pdf \n",
        "siteSelectionLinkCheckbox":"Managing Blood Sugar: http://fit4diabetes.com/files/5615/8834/9557/PBD4840_FITTP_Site_Select_2020.pdf \n",
        "lipohypertrophyLinkCheckbox":"Lipohypertrophy: http://fit4diabetes.com/files/3015/8833/4454/Patient_BD4840_FITTP_Lipohypertrophy_2020.pdf \n",
    }
    for (x in links){
        if (immutableData[x] === true){
            textForReadiness+= links[x];
            hasLink = true;
        }
    }
}

var checkbox4Text= immutableData["ONMC.specialtyTraining.otherMaterialNotes"];
if( checkbox4Text !== undefined){
      textForReadiness=textForReadiness + checkbox4Text ;
}

updatedData["hasLink"] = hasLink;
 
 

updatedData["ONMC.summary.resourcesCompiled"]=textForReadiness;

    //Your Custom Code Ends Here
    callback(updatedData);
}

