nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var q2 = immutableData["ONMC.lifestyle.ExerciseParticipation"] ? immutableData["ONMC.lifestyle.ExerciseParticipation"][0] : undefined;
var q2NoMins = immutableData["ONMC.lifestyle.ExercisePlannedMins"] ? immutableData["ONMC.lifestyle.ExercisePlannedMins"] : undefined;
var q2NoCKExe= immutableData["ONMC.lifestyle.NoExercisePlanned"];
var q2Notes = immutableData["ONMC.lifestyle.ExerciseParticipationNotes"] ? "Notes: " + immutableData["ONMC.lifestyle.ExerciseParticipationNotes"] + "\n" : "";
var q3MultiCheck = immutableData["ONMC.lifestyle.ExerciseSessionsSelections"] ? immutableData["ONMC.lifestyle.ExerciseSessionsSelections"][0] : undefined;
/*var q3Ck1 = immutableData["ONMC.lifestyle.ExerciseSessions2"] ? immutableData["ONMC.lifestyle.ExerciseSessions2"] : false;
var q3Ck2 = immutableData["ONMC.lifestyle.ExerciseSessions1"] ? immutableData["ONMC.lifestyle.ExerciseSessions1"] : false;
var q3cK3 = immutableData["ONMC.lifestyle.ExerciseSessionsNone"] ?immutableData["ONMC.lifestyle.ExerciseSessionsNone"] : false ; */
var q3Notes = immutableData["ONMC.lifestyle.ExerciseSessionsNotes"] ? "Notes: " + immutableData["ONMC.lifestyle.ExerciseSessionsNotes"] + "\n" : "";
var q4 = immutableData["ONMC.lifestyle.RDC"] ? immutableData["ONMC.lifestyle.RDC"][0] : undefined;
var q4YDate = immutableData["ONMC.lifestyle.RDCDate"] ? immutableData["ONMC.lifestyle.RDCDate"] : "";
var q4Summary = immutableData["ONMC.lifestyle.RDCSummary"] ? immutableData["ONMC.lifestyle.RDCSummary"] : "";
var q5 =immutableData["ONMC.lifestyle.SRDC"] ? immutableData["ONMC.lifestyle.SRDC"][0] : undefined;
var q5Yes= immutableData["ONMC.lifestyle.SRDCReferral"];
var q5No = immutableData["ONMC.lifestyle.SRDCNa"] ? immutableData["ONMC.lifestyle.SRDCNa"] : "";
var fat=immutableData["ONMC.lifestyle.fatNotes"] ? ": " + immutableData["ONMC.lifestyle.fatNotes"] + "\n" : "\n";
/*var otherNotes=immutableData["ONMC.lifestyle.otherNutritionTopicsNotes"] ? ": " + immutableData["ONMC.lifestyle.otherNutritionTopicsNotes"] + "\n" : "\n";


var q7Key = ["glyIndex","eatingPr","tipsEatingAway","otherNutritionTopicsCk"]
var q7Val = [
    "Glycemic index \n",
    "Diabetes Canada and Health Canada Food Guide healthy eating principles\n  ",
    "Tips for eating away from home\n",
    "Other" + otherNotes + "\n"
]*/
var q6Selections=immutableData["ONMC.lifestyle.nutritionCounselling"];

/*var q7Selections="";
for (x = 0 ; x<4 ; x++){
    if(immutableData["ONMC.lifestyle." + q7Key[x]] === true){
        q7Selections+=q7Val[x];
    }
}*/


var textforMyGoals="";
var textForAddNotesPLifestyle = "";
var textForExerciseregimen = "";
var textForDietConcerns="";
var myGoalsFromChol = immutableData["ONMC.summary.goalsForChol"] ? immutableData["ONMC.summary.goalsForChol"] : "" ;
var completedGoals = "";
//QUESTION 1


if(q2 === "Yes"){
    textForExerciseregimen = "Engages in ≥ 150 minutes per week of moderate- to vigorous-intensity aerobic exercise. \n";
}else if (q2 === "No" && q2NoMins !== undefined ) {
    textForExerciseregimen = "Engages in " +  q2NoMins +  " minutes per week of exercise or planned physical activity. Recommended ≥ 150 minutes per week of moderate- to vigorous-intensity aerobic exercise.\n"
}else if (q2 === "No" && q2NoCKExe === true ) {
    textForExerciseregimen = "No exercise or planned physical activity. Recommended ≥ 150 minutes per week of moderate- to vigorous-intensity aerobic exercise. \n";
}
textForExerciseregimen = textForExerciseregimen + q2Notes ; 
/*if (q3MultiCheck !== undefined) {
    textForExerciseregimen = textForExerciseregimen + q3MultiCheck + "\n";
}

if(q3Ck1 === true){
    textForExerciseregimen = textForExerciseregimen +  "Engages in ≥ 2 sessions per week of strength/resistance training.\n"
} else if(q3Ck2 === true){
    textForExerciseregimen = textForExerciseregimen +  "Engages in 1 session per week of strength/resistance training. Recommended ≥ 2 sessions per week of strength/resistance training.\n"
}else if(q3cK3 === true){
   textForExerciseregimen = textForExerciseregimen + "Does not currently engage in strength/resistance training. Recommended ≥ 2 sessions per week of strength/resistance training.\n"
}

textForExerciseregimen = textForExerciseregimen + q3Notes ; 
*/

if (q4 === "Yes"){
    textForDietConcerns = "Patient last consulted a registered dietitian (RD): " + q4YDate +
    "\nSummary of RD consultation: " + q4Summary + "\n";
}

if (q5 === "Yes"){
    textForDietConcerns= textForDietConcerns + "Patient has not seen a registered dietitian (RD) but is recommended to see one.\n"
}else if (q5 === "No"){
    textForDietConcerns= textForDietConcerns +  "Patient has not seen a registered dietitian and does not wish to do so at this time. Patient’s reason: " + q5No + "\n"
}

if (q6Selections === true){
    textForDietConcerns= textForDietConcerns + "Counselled the patient on portion sizes when planning a meal (handy portion guide).\n"
}
/*if(q7Selections.length > 0){
    textForDietConcerns= textForDietConcerns +  "Follow-up consultations to discuss: \n" + q7Selections +
    "Learn more about nutrition and healthy eating to better manage my diabetes. \n"
    textforMyGoals=   textforMyGoals + "Follow-up consultations to discuss: \n" + q7Selections +
    "Learn more about nutrition and healthy eating to better manage my diabetes. \n"
}*/


if (myGoalsFromChol.length > 0) {
    completedGoals = myGoalsFromChol + "\n" + textforMyGoals ; 
} else {
    completedGoals = textforMyGoals ; 
}

var showExercise = immutableData["showExercise"] ?  immutableData["showExercise"][0] : "No"; 
if (textForAddNotesPLifestyle.length > 0 && showExercise == "Yes" ){
    updatedData["ONMC.lifestyle.notes"] = textForAddNotesPLifestyle;
    updatedData["ONMC.lifestyle.notesCheck"] = true;
}else {
    updatedData["ONMC.lifestyle.notes"] = undefined;
    updatedData["ONMC.lifestyle.notesCheck"] = undefined;
}

if (textForExerciseregimen.length > 0 && showExercise == "Yes"  ){
    updatedData["ONMC.lifestyle.exerciseRegimen"] = textForExerciseregimen;
    updatedData["ONMC.lifestyle.exerciseOverallCheck"] = true;
} else {
    updatedData["ONMC.lifestyle.exerciseRegimen"] = undefined;
    updatedData["ONMC.lifestyle.exerciseOverallCheck"] = undefined;
}
if (textForDietConcerns.length > 0 && showExercise == "Yes"  ){
    updatedData["ONMC.general.lifestyleDietNotes"] = textForDietConcerns;
    updatedData["ONMC.lifestyle.patientDietCheck"] = true;
}else {
    updatedData["ONMC.general.lifestyleDietNotes"] = undefined;
    updatedData["ONMC.lifestyle.patientDietCheck"] = undefined;
}

if (completedGoals.length > 0 && showExercise == "Yes"  ){
    updatedData["ONMC.summary.goalsCompiled"] = completedGoals;
    updatedData["ONMC.summary.goalsFromExercise"]  = completedGoals ;
}else {
        updatedData["ONMC.summary.goalsCompiled"]  = myGoalsFromChol ;
        updatedData["ONMC.summary.goalsFromExercise"]  = myGoalsFromChol ;
}

    //Your Custom Code Ends Here
    callback(updatedData);
}

nodes.customScss:
div.accordion-checkbox {

    div.checkbox-field {
        margin: 20px 0 16px 0 !important;
        md-input-container {
            margin: -10px 0 0 0 !important;
        }
        md-checkbox .md-container {
            visibility: hidden !important;
        }
        md-checkbox.ng-empty .md-container:before {
            content: "\f055" !important;
            font-size: 20px;
            color: #EC6453;
            top: 1px;
            left: 5px;
            font-family: "fontAwesome";
            position: absolute;
            visibility: visible;
        }

        md-checkbox.md-checked .md-container:before {
            content: "\f056" !important;
            font-size: 20px;
            color: #EC6453;
            top: 1px;
            left: 5px;
            font-family: "fontAwesome";
            position: absolute;
            visibility: visible;
        }
        md-checkbox .md-label .checkbox-label {
            font-size: 18px !important;
            font-weight: 600 !important;
        }
    }
}

div.accordion-info {
    .accordion-img {
        margin: 3% 0 3% 4%; 
        width: 35%;
    }
    .accordion-p {
        margin-left: 4%;
        font-size: 14px;
    }
}
div.checkbox-field md-input-container {
    margin: 5px 0 !important;
}

.multi-line div.checkbox-field md-input-container {
    margin: 15px 0 !important;
}

.special-special-check .grid-wrapper md-grid-tile {
    width:auto!important;
    height:auto!important;
}
.special-special-check .grid-wrapper md-grid-tile .md-label {
    width:50000%;
    text-align:left;
    margin-left: 10px;
    font-weight: 400;
}
.special-special-check .md-checked .md-label span {
    color: #000 !important;
}
.special-special-check .multicheck-grid .md-checked:not([disabled]) {
    background: none !important;
}
.special-special-check .multicheck-grid .md-checked:not([disabled]):after {
    box-sizing: border-box;
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
    position: absolute;
    left: 4.67px;
    top: .22px;
    display: table;
    width: 6.67px;
    height: 13.33px;
    border-width: 2px;
    border-style: solid;
    border-top: 0;
    border-left: 0;
    content: '';
}
.special-special-check md-input-container.fh-input-container {
    margin-top: 0 !important;
}

.special-special-check md-grid-list {
    height: 10px !important;
}

.margin-left-30 {
    margin-left: 30px !important;
}

// for fixing the checkbox colour issue when launched from landing page
.special-special-check .multicheck-grid .md-checked:not([disabled]):after { 
    color: #2E3142 !important;
}

// fixing styling on number fields
div.number-field fieldset {
    margin-left: 0px;
    padding-left: 0px;
}

// number field
div[number-field] {
    fieldset {
        padding-left: 0px !important;
        padding-right: 0px !important;
        margin-left: 0px !important;
    }

    md-input-container {
        padding: 0px !important;

        input[type=text] {
            padding-bottom: 6px !important;
        }
    }
}

.unit {
    .fh-dropdown-container {
        margin-top: 28px !important; 
    }
    &.margin-top-8 {
        .fh-dropdown-container {
            margin-top: 8px !important;
        }
    }
}

.margin-top-0 {
    md-input-container {
        margin-top: 0px !important;
    }

}

table,th,td{
    border: 1px solid black;
  border-collapse: collapse;
}
th{
    background:#eeeee4;
    text-align:center;
    font-size:14px;
}

