nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    //VARIABOE Initialization
var question1 = immutableData["ONMC.lifestyle.bgMonitor"] ? immutableData["ONMC.lifestyle.bgMonitor"][0] : undefined;
var q1YesPre = immutableData["ONMC.lifestyle.bgPremmol"] ?"\nBlood Glucose (sugar) targets - Pre-prandial/fasting BG: " + immutableData["ONMC.lifestyle.bgPremmol"] +"mmol/L"  : undefined;
var q1Yes2Hr = immutableData["ONMC.lifestyle.bgPostmmol"] ? "Blood Glucose (sugar) targets - 2-hour post-prandial BG: "+  immutableData["ONMC.lifestyle.bgPostmmol"] + " mmol/L  " : undefined;
var q1YesNotes = immutableData["ONMC.lifestyle.bgMNotes"] ? "Notes: " + immutableData["ONMC.lifestyle.bgMNotes"] + "\n" : "";
var q1NoNotes = immutableData["ONMC.lifestyle.bgNoNotes"] ? "Notes: " + immutableData["ONMC.lifestyle.bgNoNotes"] + "\n": "";      


var q2 = immutableData["ONMC.lifestyle.hyGlyRisk"] ? immutableData["ONMC.lifestyle.hyGlyRisk"][0] : undefined;
/*var q2No = immutableData["ONMC.lifestyle.hyGlyRiskOptions"] ? immutableData["ONMC.lifestyle.hyGlyRiskOptions"][0] + "\n" : "";
var q2No1 = immutableData["ONMC.lifestyle.hyGlyRiskOptions"] ? immutableData["ONMC.lifestyle.hyGlyRiskOptions"][1] + "\n" : "";
var q2Compiled = q2No +"\n" + q2No1; */ 
var q2Signs = immutableData["ONMC.lifestyle.hyGlySigns"] ? immutableData["ONMC.lifestyle.hyGlySigns"][0] : undefined;
var q2Treatment = immutableData["ONMC.lifestyle.hyGlyTreatment"] ? immutableData["ONMC.lifestyle.hyGlyTreatment"][0] : undefined;
var q2Notes = immutableData["ONMC.lifestyle.hyGlyRiskOtherCk"] ? immutableData["ONMC.lifestyle.hyGlyRiskOtherCk"][0]: undefined;
var q2NotesText=immutableData["ONMC.lifestyle.hyGlyRiskNotes"] ? "Notes: " + immutableData["ONMC.lifestyle.hyGlyRiskNotes"] : undefined;
var q2Compiled = "";

//Question 1 : Goes to Testing Frequency and optimal schedule ONMC.training.testFrequencyNotesCompiled(Static field without user control) , ONMC.training.testFrequency[]
if (question1 !== undefined){
    if(question1 === "Yes"){
        if (q1YesNotes.length > 0 )
        {
            updatedData["ONMC.training.testFrequency"] = ["Yes"];
            //updatedData["ONMC.training.testFrequencyNotesCompiled"] = q1Yes2Hr + q1YesPre + "\n" + q1YesNotes ;
            updatedData["ONMC.training.testFrequencyNotesCompiled"] = q1YesNotes ;
        }else {
            var testingNotes = immutableData["ONMC.training.testFrequencyNotes"];
            if (testingNotes === undefined || testingNotes === "") {
                updatedData["ONMC.training.testFrequency"] = undefined;
            }
    
            updatedData["ONMC.training.testFrequencyNotesCompiled"] = undefined ;
        }
      
       
        updatedData["ONMC.lifestyle.bgNoNotes"] = undefined;
    }else if (question1 === "No"){
        updatedData["ONMC.training.testFrequency"] = ["Yes"];
        updatedData["ONMC.training.testFrequencyNotesCompiled"] ="Patient does not currently check their blood glucose levels.\n" + q1NoNotes; 
        updatedData["ONMC.lifestyle.bgPremmol"] = undefined;
        updatedData["ONMC.lifestyle.bgPostmmol"] = undefined;
        updatedData["ONMC.lifestyle.bgMNotes"] = undefined;
    }
}else {
    updatedData["ONMC.lifestyle.bgPremmol"] = undefined;
    updatedData["ONMC.lifestyle.bgPostmmol"] = undefined;
    updatedData["ONMC.lifestyle.bgMNotes"] = undefined;
    updatedData["ONMC.lifestyle.bgNoNotes"] = undefined;
       var testingNotes = immutableData["ONMC.training.testFrequencyNotes"];
        if (testingNotes === undefined || testingNotes === "") {
           updatedData["ONMC.training.testFrequency"] = undefined;
        }
    
    updatedData["ONMC.training.testFrequencyNotesCompiled"] = undefined ;

}

//Question 2 : ONMC.training.hypoglycemiaNotesCompiled(Static readonly field) , ONMC.training.hypoglycemia[0]
if(q2 !== undefined) {
    if(q2 === "Yes"){
        q2Compiled = "Patient is able to recognize signs and symptoms of HYPOglycemia and understands how to treat it.\n";

  } else if (q2 === "No"){
        q2Compiled = "Patient not currently at risk for HYPOglycemia. \n ";      
  
}


}
if (q2Notes === "Other" && q2NotesText !== undefined ) {
    q2Compiled =  q2Compiled + q2NotesText;
}


if (q2Compiled.length > 0 ){
    updatedData["ONMC.training.hypoglycemia"] = ["Yes"];
    updatedData["ONMC.training.hypoglycemiaNotesCompiled"] = q2Compiled;
}
else {
   
        var hypoNotes = immutableData["ONMC.training.hypoglycemiaNotes"];
        if (hypoNotes === undefined || hypoNotes === "") {
            updatedData["ONMC.training.hypoglycemia"] = undefined;
        }
   updatedData["ONMC.training.hypoglycemiaNotesCompiled"] = undefined;
}

    //Your Custom Code Ends Here
    callback(updatedData);
}

