nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var recUse = immutableData["ONMC.lifestyle.usesDrugs"] ? immutableData["ONMC.lifestyle.usesDrugs"][0] : undefined;
var recText = immutableData["ONMC.lifestyle.drugFrequencyOfUse"] ? immutableData["ONMC.lifestyle.drugFrequencyOfUse"] : "";
var alcoUse = immutableData["ONMC.lifestyle.alcoholUse"] ? immutableData["ONMC.lifestyle.alcoholUse"][0] : undefined;
var alcoCk = immutableData["ONMC.lifestyle.drugConselled"];

var tobacoUse = immutableData["ONMC.lifestyle.smokesTobacco"] ? immutableData["ONMC.lifestyle.smokesTobacco"][0] : undefined;
var cigUse = immutableData["ONMC.lifestyle.tobaccoDailyUse"] ? immutableData["ONMC.lifestyle.tobaccoDailyUse"] : "";
var readinessOfChange=immutableData["ONMC.lifestyle.readiness"] ? immutableData["ONMC.lifestyle.readiness"][0] : undefined;
var readinessNotes = immutableData["ONMC.lifestyle.readinessNotes"] ? "Stage Of Readiness Notes:" + immutableData["ONMC.lifestyle.readinessNotes"] + "\n" : "";

var quitSmokingMulti = immutableData["ONMC.lifestyle.quitPlan"] ? immutableData["ONMC.lifestyle.quitPlan"][0] : undefined;

var quitYDetails = immutableData["ONMC.lifestyle.quitPlanDetailsY"] ? "Quit plan: " + immutableData["ONMC.lifestyle.quitPlanDetailsY"] + "\n" : "";
var quitYSmokingCk = immutableData["ONMC.lifestyle.quitPlanSmoke"];
var quitSmokingNotesY = immutableData["ONMC.lifestyle.quitPlanSmokeNotes"] ? "Details: " + immutableData["ONMC.lifestyle.quitPlanSmokeNotes"] + "\n" :"";
var quitPatientReqY = immutableData["ONMC.lifestyle.quitPlanFollowY"];
var quitPatientReqYNotes = immutableData["ONMC.lifestyle.quitPlanFollowNotes"] ? "Details: " + immutableData["ONMC.lifestyle.quitPlanFollowNotes"] + "\n": "";

var quitNoSmoke = immutableData["ONMC.lifestyle.quitPlanSmokeCess"];
var quitNoSmokeNotes = immutableData["ONMC.lifestyle.quitPlanDetailsN"] ? "Details of quit plan:" + immutableData["ONMC.lifestyle.quitPlanDetailsN"]  + "\n": "";
var quitNoSmokeFollowUp = immutableData["ONMC.lifestyle.quitPlanFollowN"];
var contemplation = immutableData["ONMC.lifestyle.scCounc"] ? immutableData["ONMC.lifestyle.scCounc"][0] : undefined;

var contemplationYSmokingCK = immutableData["ONMC.lifestyle.scProvided"];
var contemplationYSmokeDetails = immutableData["ONMC.lifestyle.scProvidedDetails"] ? "Smoking cessation counselling provided: " + immutableData["ONMC.lifestyle.scProvidedDetails"] + "\n": "";
var contemplationYSmokeGoals = immutableData["ONMC.lifestyle.scProvidedGoals"] ? immutableData["ONMC.lifestyle.scProvidedGoals"] : "";
var contemplationYSmokeAP = immutableData["ONMC.lifestyle.scProvidedActionPlan"] ? "Smoking cessation counselling provided: " +  immutableData["ONMC.lifestyle.scProvidedActionPlan"] + "\n": "";

var contemplationYFollow = immutableData["ONMC.lifestyle.scProvidedFollowUp"];
var contemplationYFollowNotes = immutableData["ONMC.lifestyle.scProvidedFollowUpDetails"] ?"Patient requires follow-up consultation to discuss smoking cessation strategies"+ immutableData["ONMC.lifestyle.scProvidedFollowUpDetails"] + "\n" : "";

var contemplatingNoNotes = immutableData["ONMC.lifestyle.scNoNotes"] ? "Other Notes: " + immutableData["ONMC.lifestyle.scNoNotes"] + "\n": "";

var goalsText="";
var stageReadinessText="";
var smokingCessationText="";
var whatWillIDoText="";
//Smoking cessation binding = ONMC.lifestyle.tobaccoCessationOfferedNotes
//Stage of readfiness binding = ONMC.general.readiness
//Goals binding = ONMC.summary.goals
if(tobacoUse !== undefined){
    if(tobacoUse === "No"){
        updatedData["ONMC.lifestyle.tobacco"] = immutableData["ONMC.lifestyle.smokesTobacco"] ;
        updatedData["ONMC.lifestyle.tobaccoCessationOfferedNotes"] = undefined;
    } else if (tobacoUse === "Yes"){
        updatedData["ONMC.lifestyle.tobacco"] = ["Yes, currently"];
        //updatedData["ONMC.lifestyle.tobaccoNumber"] = cigUse;
        if (readinessOfChange !== undefined){
            stageReadinessText = readinessOfChange + "\n";
            goalsText = readinessOfChange + "\n";
            smokingCessationText = smokingCessationText + readinessOfChange + "\n";
            if (readinessOfChange === "Patient is motivated and ready to quit smoking."){
                if(quitSmokingMulti === "Yes"){
                    smokingCessationText = smokingCessationText + quitYDetails ;
                    whatWillIDoText = whatWillIDoText + quitYDetails;
                    if (quitYSmokingCk === true){
                        smokingCessationText = smokingCessationText +  quitSmokingNotesY ; 
                        whatWillIDoText = whatWillIDoText + quitSmokingNotesY ; 
                    }
                    if (quitPatientReqY === true){
                        whatWillIDoText = whatWillIDoText + "Patient requires follow-up consultation to discuss smoking cessation strategies. \n";
                        smokingCessationText = smokingCessationText + "Patient requires follow-up consultation to discuss smoking cessation strategies. \n";
                    }
 
                }else  if(quitSmokingMulti === "No") {
                    smokingCessationText = smokingCessationText + quitYDetails ;
                    whatWillIDoText = whatWillIDoText + quitYDetails;
                    if (quitNoSmoke === true){
                        smokingCessationText = smokingCessationText +"Smoking cessation counselling provided, quit plan created with patient. "+quitNoSmokeNotes ; 
                        whatWillIDoText = whatWillIDoText + "Smoking cessation counselling provided, quit plan created with patient. "+ quitNoSmokeNotes ; 
                    }
                    if (quitNoSmokeFollowUp === true){
                        whatWillIDoText = whatWillIDoText + "Patient requires follow-up consultation to discuss a quit plan and smoking cessation strategies.\n";
                        smokingCessationText = smokingCessationText + "Patient requires follow-up consultation to discuss a quit plan and smoking cessation strategies. \n";
                    }
                }
            }else if (readinessOfChange === "Patient is contemplating making changes but is not ready to quit smoking at this time."
                  || readinessOfChange === "Patient is not considering quitting smoking at this time." ){
                    if(contemplation ==="Yes"){
                        if(contemplationYSmokingCK === true){
                            whatWillIDoText = whatWillIDoText + contemplationYSmokeAP;
                            goalsText = goalsText + contemplationYSmokeDetails;
                            smokingCessationText = smokingCessationText + contemplationYSmokeDetails;
                        }
                        if (contemplationYFollow === true){
                            smokingCessationText = smokingCessationText + "Patient requires follow-up consultation to discuss smoking cessation strategies.\n";
                        }
                    }else if (contemplation === "No"){
                        smokingCessationText = smokingCessationText + "Patient is not receptive to smoking cessation counselling at this time.\n";
                    }
                    

                  }
        }

    }
    if(smokingCessationText.length > 0){
        updatedData["ONMC.lifestyle.tobaccoCessationOfferedNotes"] = smokingCessationText;
    }

}
else {
    updatedData["ONMC.lifestyle.tobacco"] = undefined;
    updatedData["ONMC.lifestyle.tobaccoCessationOfferedNotes"] = undefined;
    //updatedData["ONMC.lifestyle.tobaccoNumber"] = undefined;

}
var previousMyGoals = immutableData["ONMC.summary.goalsFromExercise"] ? immutableData["ONMC.summary.goalsFromExercise"] + "\n":"";
console.log("Before Concat\n" + goalsText);
console.log("Before , previous goals\n" + previousMyGoals );
var summaryAndGoals  =  previousMyGoals + goalsText  ;
console.log("After Concat\n" + summaryAndGoals )

updatedData["ONMC.summary.goalsCompiled"]=summaryAndGoals;
updatedData["ONMC.summary.goalsFromSmoking"]=summaryAndGoals;

updatedData["ONMC.general.readinessCompiled"] = stageReadinessText ;
updatedData["ONMC.general.readinessFromSmoking"] = stageReadinessText ;

updatedData["ONMC.summary.whatIWillDoCompiled"]=whatWillIDoText;
updatedData["ONMC.summary.whatIWillDoFromSmoking"] = whatWillIDoText;











if(alcoUse !== undefined){
    updatedData["ONMC.lifestyle.alcohol"] = immutableData["ONMC.lifestyle.alcoholUse"];
    if (alcoUse == "Yes" && alcoCk === true){
        updatedData["ONMC.lifestyle.alcoholFrequency"] = "Counselled patient on key takeaways from Canada’s low-risk alcohol drinking guidelines (Aug 2022).";
    }else {
        updatedData["ONMC.lifestyle.alcoholFrequency"]  = undefined;
    }
} else {
    updatedData["ONMC.lifestyle.alcohol"]=  undefined;
    updatedData["ONMC.lifestyle.alcoholFrequency"]  = undefined;
}

if (recUse !== undefined){
    updatedData["ONMC.lifestyle.recDrugs"] = immutableData["ONMC.lifestyle.usesDrugs"] ;
    if(recUse =="Yes"){
        updatedData["ONMC.lifestyle.recDrugsFrequency"]=recText;
    }else {
        updatedData["ONMC.lifestyle.recDrugsFrequency"] = undefined;
    }

}else {
    updatedData["ONMC.lifestyle.recDrugs"] = undefined;
    updatedData["ONMC.lifestyle.recDrugsFrequency"] = undefined;
}

    //Your Custom Code Ends Here
    callback(updatedData);
}

