nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var ck1=immutableData["motivatedOpenToMakingChanges"];
var ck2=immutableData["contemplatingNotReadyToMakingChanges"];
var ck3=immutableData["notConsideringMakingChanges"];
var ckOther=immutableData["ONMC.lifestyle.selfReadyOther"];
var ckNotes = immutableData["ONMC.lifestyle.selfReadyOtherNotes"] ? immutableData["ONMC.lifestyle.selfReadyOtherNotes"] : undefined;

var selfck1 = immutableData["ONMC.lifestyle.regressionDiabetes"];
var selfOther = immutableData["ONMC.lifestyle.regressionOther"];
var selfOtherNotes = immutableData["ONMC.lifestyle.regressionOtherNotes"] ? immutableData["ONMC.lifestyle.regressionOtherNotes"] : undefined;

var answeredSelf=false;

var barriers = immutableData["ONMC.lifestyle.barriers"] ? "Barriers to Achieving Patient Goals / Action Plan to Overcome Barrier: " + immutableData["ONMC.lifestyle.barriers"]  + "\n": undefined;
var textForPrevReadiness=immutableData["ONMC.general.readinessFromSmoking"] ? immutableData["ONMC.general.readinessFromSmoking"] + "\n": "";
var textForPreviousGoals=immutableData["ONMC.summary.goalsFromSmoking"] ? immutableData["ONMC.summary.goalsFromSmoking"] + "\n":"";
var textForGoals = "";

if  (ck1 === true){
    updatedData["ONMC.general.readinessCompiled"] = textForPrevReadiness + "Patient is motivated and open to making changes to their diabetes management. "
}else if (ck2 === true){
    updatedData["ONMC.general.readinessCompiled"] = textForPrevReadiness + "Patient is contemplating making changes to their diabetes management but is not ready to make changes at this time."
}else if (ck3 === true ){
    updatedData["ONMC.general.readinessCompiled"] = textForPrevReadiness + "Patient is not considering making changes to their diabetes management at this time. "
}else if (ckOther === true && ckNotes !== undefined){
    updatedData["ONMC.general.readiness"] = textForPrevReadiness  + ckNotes;
}else if (textForPrevReadiness.length > 0) {
    updatedData["ONMC.general.readinessCompiled"] = textForPrevReadiness;
}else {
    updatedData["ONMC.general.readinessCompiled"] = undefined;
}

if (selfck1 == true){
    textForGoals = "Reduce the risk of diabetes progression and related complications.\n"
    answeredSelf = true;
}
if(selfOther === true && selfOtherNotes !== undefined){
    textForGoals=+ "Other: " + selfOtherNotes + "\n";
    answeredSelf = true ; 
}

textForGoals = textForPreviousGoals + textForGoals ; 

updatedData["ONMC.summary.goalsCompiled"] = textForGoals ; 
var prev = immutableData["ONMC.summary.whatIWillDoFromSmoking"] ? immutableData["ONMC.summary.whatIWillDoFromSmoking"] + "\n\n" :"";
if (answeredSelf === true && barriers !== undefined){
    updatedData["ONMC.summary.whatIWillDoCompiled"] = prev + barriers ; 
    updatedData["ONMC.summary.whatIWillDoFromSelf"] =prev +  barriers ; 
}else {
    updatedData["ONMC.summary.whatIWillDoCompiled"] = prev;
    updatedData["ONMC.summary.whatIWillDoFromSelf"] = prev ;  
}

    //Your Custom Code Ends Here
    callback(updatedData);
}

