nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var key = ["exercise","exerciseStrength","diet","bloodsugar","checkLevels","footExam","eyeExam","otherCK"];
var freqNotes= "\n";
var otherNotes=immutableData["ONMC.aap.otherNotes"] ? ": " + immutableData["ONMC.aap.otherNotes"] + "\n" : "\n";
var frequency = immutableData["ONMC.aap.freq"];
if(frequency === true){freqNotes = immutableData["ONMC.aap.freqText"] ? ",Frequency: " + immutableData["ONMC.aap.freqText"] + "\n" : "\n";}

var value = [
    "At least 150 minutes per week of moderate- to vigorous-intensity aerobic exercise, ideally spread over at least 3 days of the week, with no more than 2 consecutive days without exercise (e.g., walking, jogging, cycling, etc.).\n",
    "In addition to aerobic exercise, at least 2 sessions per week of strength/resistance training (e.g., exercises with weights), preferably 3 times per week.\n",
    "Consult with a registered dietitian on diet and nutrition.\n",
    "Check blood sugar routinely and as needed." + freqNotes ,
    "Get A1C, blood glucose levels, blood pressure, and cholesterol checked, and report results to the healthcare team regularly. \n",
    "Routine foot examinations with a healthcare provider (at least annually). \n",
    "Routine eye examinations with a vision care professional (at least annually). \n",
    "Other" + otherNotes
];
var whatWillIText="";
var prevWhatWillIDo = immutableData["ONMC.summary.whatIWillDoFromSelf"] ? immutableData["ONMC.summary.whatIWillDoFromSelf"] + "\n\n" : "";
for (x = 0 ; x<8 ; x++){
    if(immutableData["ONMC.aap." + key[x]] === true){
        whatWillIText+=value[x];
    }
}
whatWillIText = prevWhatWillIDo + whatWillIText;

if (whatWillIText.length > 0 ){
    updatedData["ONMC.summary.whatIWillDoCompiled"] = whatWillIText ;
     updatedData["ONMC.summary.whatIWillDoAAP"] = whatWillIText ;
}else {
    updatedData["ONMC.summary.whatIWillDoCompiled"] = undefined ;
    updatedData["ONMC.summary.whatIWillDoAAP"] = undefined ; 
}

    //Your Custom Code Ends Here
    callback(updatedData);
}

function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    let vaccineArr = ['tetanus', 'influenza', 'covid', 'pneumococcal', 'shingles','rsv','meningococcal','hepatitisB', 'hepatitisA','haemophilus','pertussis','travel', 'other'];
    let vaccineNotes = "";
    var generateVaxxPO = false;
    for(let i of vaccineArr) {
        let vaccine = immutableData[`immunization.${i}`];
        console.log("vaccine" + vaccine);
        let vaccineStr = i.charAt(0).toUpperCase() + i.slice(1);
        console.log("vaccine STR " + vaccineStr);generateVaxxPO
        if(Array.isArray(vaccine)) {
            if(vaccine[0] == 'Yes' || vaccine[0] == 'No') {
                // mapping vaccines and notes
                if(immutableData[`immunization.${i}Notes`]) {
                    console.log("Inside with notes");
                    if(vaccineStr == "Travel") {vaccineStr = "International travel"}
                    if(vaccineStr == "Rsv") {vaccineStr = "Respiratory Syncytial Virus"}
                    if(vaccineStr == "HepatitisB") {vaccineStr = "Hepatitis B"}
                    if(vaccineStr == "HepatitisA") {vaccineStr = "Hepatitis A"}
                    if(vaccineStr == "Haemophilus") {vaccineStr = "Haemophilus influenzae type B (Hib)"}
                    if(vaccineStr == "Pertussis") {vaccineStr = "Pertussis (ap)"}
                    vaccineNotes += vaccineStr + ': ' + vaccine[0] + ' - ' + immutableData[`immunization.${i}Notes`] + '\n';
                } else {
                    console.log("Inside of other with no notes ");
                    if(vaccineStr == "Travel") {vaccineStr = "International travel"}
                    if(vaccineStr == "Rsv") {vaccineStr = "Respiratory Syncytial Virus"}
                    if(vaccineStr == "HepatitisB") {vaccineStr = "Hepatitis B"}
                    if(vaccineStr == "HepatitisA") {vaccineStr = "Hepatitis A"}
                    if(vaccineStr == "Haemophilus") {vaccineStr = "Haemophilus influenzae type B (Hib)"}
                    if(vaccineStr == "Pertussis") {vaccineStr = "Pertussis (ap)"}
                    vaccineNotes += vaccineStr + ': ' + vaccine[0] + '\n';
                }
                console.log("ARR vaccineArr[i]\t" + vaccineStr);
                console.log("vacc accine[0] \t" + vaccine[0]);
                if (vaccineStr == 'Pneumococcal' && vaccine[0] == 'No'){
                    console.log("inside penumo");
                    var opt= immutableData["immunization.pneumococcalRecc"] ? immutableData["immunization.pneumococcalRecc"][0] : undefined;
                    var sendPO= immutableData["immunization.pneumococcalSendPO"] ? immutableData["immunization.pneumococcalSendPO"][0] : undefined;
                    if (opt === "Yes"){
                        vaccineNotes += "Patient has not received Pneumococcal vaccination – recommended\n";
                        if (sendPO !== undefined){generateVaxxPO = true;}
                    }else if (opt === "No"){
                          vaccineNotes += "Patient has not received Pneumococcal vaccination – Not recommended\n";
                    }
                }
                 if (vaccineStr == 'Shingles' && vaccine[0] == 'No'){
                     console.log("inside shigoles ")
                    var opt= immutableData["immunization.shinglesRecc"] ? immutableData["immunization.shinglesRecc"][0] : undefined;
                    var sendPO= immutableData["immunization.shinglesSendPO"] ? immutableData["immunization.shinglesSendPO"][0] : undefined;
                    if (opt === "Yes"){
                        vaccineNotes += "Patient has not received Shingles vaccination – recommended Shingles vaccination. \n";
                        if (sendPO !== undefined){generateVaxxPO = true;}
                    }else if (opt === "No"){
                          vaccineNotes += "Patient has not received Shingles vaccination – Not recommended\n";
                    }
                 }
                if (vaccineStr == 'Respiratory Syncytial Virus' && vaccine[0] == 'No'){
                     console.log("inside shigoles ")
                    var opt= immutableData["immunization.recommendRsv"] ? immutableData["immunization.recommendRsv"][0] : undefined;
                    var sendPO= immutableData["immunization.rsvSendPO"] ? immutableData["immunization.rsvSendPO"][0] : undefined;
                    if (opt === "Yes"){
                        vaccineNotes += "Patient has not received Respiratory Syncytial Virus vaccination – recommended Respiratory Syncytial Virus vaccination. \n";
                         if (sendPO !== undefined){generateVaxxPO = true;}
                    }else if (opt === "No"){
                          vaccineNotes += "Patient has not received Respiratory Syncytial Virus vaccination – Not recommended\n";
                    }
                }
            } else {
                // backtracking
                updatedData[`immunization.${i}Notes`] = '';
            }   
        } 
    }
    
    
    
    
    var location= immutableData["ONMC.location"][0] ;
    var virText = "";
    if (location == "Virtual") {
        var virtualComment = immutableData["ONMC.locationVirtual"] ? immutableData["ONMC.locationVirtual"] : undefined;
        virText = "Reason for virtual MedsCheck: ";
        var otherVirNotes = immutableData["ONMC.locationVirtualOther"] ? immutableData["ONMC.locationVirtualOther"] : "";
        var partialResult = "";
        if (virtualComment !== undefined && virtualComment.length > 0 ){
            var removeOther = virtualComment.filter(value => value !== "Other");
            partialResult = removeOther.join(', ');

            if (virtualComment.includes("Other")){
                partialResult= partialResult + ", " + otherVirNotes;
            }
            partialResult = partialResult + ", "+  immutableData["ONMC.locationOtherNotes"] + "." ;
            virText = virText + partialResult + "\n\n" ; 
            }
        }
if(vaccineNotes.length > 0 ){
    vaccineNotes = "Vaccines/Immunizations: \n" + vaccineNotes;
    
}
    var ynq = immutableData["showVaccineInfo"] ? immutableData["showVaccineInfo"][0] : "No";
    if (ynq === "No"){
          var Finaltext = virText;
          updatedData['generateVaxxPO'] = false;
          
    }else {
          var Finaltext = virText + vaccineNotes.replace(/undefined /g,'');
          updatedData["generateVaxxPO"] = generateVaxxPO;
    }
  
    if (Finaltext.length > 0){
       updatedData['additionalInfo'] =["Yes"];
    updatedData['additionalInfoTextCompiled'] =  Finaltext;
    }else {
        var regAI = immutableData["additionalInfoText"] ? immutableData["additionalInfoText"] : "";
