nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var hideShow = immutableData["showOptionalScreeningSection"] ? immutableData["showOptionalScreeningSection"][0] : "No";

if (hideShow === "Yes") {
    var q3 = immutableData["ONMC.lifestyle.labWork"] ? immutableData["ONMC.lifestyle.labWork"][0] : undefined;
    var q3NANotes = immutableData["ONMC.lifestyle.labWorkNotAppNotes"] ? "Not applicable: " + immutableData["ONMC.lifestyle.labWorkNotAppNotes"] : "";
    var q3NoCK2 = immutableData["ONMC.lifestyle.labWorkRequires"];
    var q3NoCK1 = immutableData["ONMC.lifestyle.labWorkRecommend"];
    var q3Notes = immutableData['ONMC.lifestyle.labWorkNotes'];
    var q3String = "";

    var q4 = immutableData["ONMC.lifestyle.footRoutine"] ? immutableData["ONMC.lifestyle.footRoutine"][0] : undefined;
    var q4Yes = immutableData["ONMC.lifestyle.footRoutineFrequency"] ? immutableData["ONMC.lifestyle.footRoutineFrequency"] : "";
    var q4Ck1 = immutableData["ONMC.lifestyle.footRoutineRecommend"];
    var q4Ck2 = immutableData["ONMC.lifestyle.footRoutineRequires"];
    var q4Notes = immutableData["ONMC.lifestyle.footRoutineNotApp"] ? "Not applicable: " + immutableData["ONMC.lifestyle.footRoutineNotApp"] : "";
    var q4String = "";

    var q5 = immutableData["ONMC.lifestyle.visionAppt"] ? immutableData["ONMC.lifestyle.visionAppt"][0] : undefined;
    var q5Yes = immutableData["ONMC.lifestyle.visionApptFrequency"] ?": " +  immutableData["ONMC.lifestyle.visionApptFrequency"] : "";
    var q5Ck1 = immutableData["ONMC.lifestyle.visionApptRecommend"];
    var q5Ck2 = immutableData["ONMC.lifestyle.visionApptRequire"];
    var q5Notes = immutableData["ONMC.lifestyle.visionApptSpecify"] ? "Not Applicable: " + immutableData["ONMC.lifestyle.visionApptSpecify"] : "";
    var q5String = "";

    var q6 = immutableData["ONMC.lifestyle.sdPlan"] ? immutableData["ONMC.lifestyle.sdPlan"][0] : undefined;
    var q6Yes = immutableData["ONMC.lifestyle.sdPlanDetails"] ? immutableData["ONMC.lifestyle.sdPlanDetails"] : undefined;
    var q6Ck1 = immutableData["ONMC.lifestyle.sdPlanRequires"];
    var q6NaNotes = immutableData["ONMC.lifestyle.sdPlanSpecifyReason"] ? "Not applicable: " + immutableData["ONMC.lifestyle.sdPlanSpecifyReason"] : "";
    var q6Notes = immutableData["ONMC.lifestyle.sdPlanNotes"];
    var q6String = "";

    var q7 = immutableData["ONMC.lifestyle.injSit"] ? immutableData["ONMC.lifestyle.injSit"][0] : undefined;
    var q7Yes = immutableData["ONMC.lifestyle.injSitDetails"] ? immutableData["ONMC.lifestyle.injSitDetails"] : undefined;
    var q7Ck1 = immutableData["ONMC.lifestyle.injSitRecommend"];
    var q7NaNotes = immutableData["ONMC.lifestyle.injSitSpecifyReason"] ? "Not applicable: " + immutableData["ONMC.lifestyle.injSitSpecifyReason"] : "";
    var q7Notes = immutableData["ONMC.lifestyle.injSitNotes"];
    var q7String = "";


    //Question 3 : ONMC.training.otherSMBGNotes , ONMC.training.otherSMBG[0]
    console.log("Q3" + q3)
    if (q3 !== undefined || q3NoCK1 === true || q3NoCK2 === true) {
        updatedData["ONMC.training.otherSMBG"] = ["Yes"];
        if (q3 === "Yes") {
            q3String = "Patient reports they have routine lab work completed.\t";
            console.log("IN YES");
        }
        /* else if (q3 === "Not Applicable") {
                        updatedData["ONMC.training.otherSMBGNotesCompiled"] = q3NANotes;
                        console.log("IN N/A");
                    } */
        else if (q3 === "No") {
            q3String = "Patient reports they do not have routine lab work completed.\t"
            console.log("IN NO");

        }
        if (q3NoCK1 === true) {
            q3String += "Recommended to patient to have routine lab work completed to monitor glycemic control and disease progression.\t"
        }
        if (q3NoCK2 === true) {
            q3String += "Patient requires a follow-up consultation to review diabetes targets and results (i.e., A1C, cholesterol, BP, and SMBG). \t"
        }
        console.log(q3String)
        updatedData["ONMC.training.otherSMBGNotesCompiled"] = q3String;

    } else {

        var notes = immutableData["ONMC.training.otherSMBGNotes"];
        if (notes === undefined || notes === "") {
            updatedData["ONMC.training.otherSMBG"] = undefined;
        }
        updatedData["ONMC.training.otherSMBGNotesCompiled"] = undefined;
    }
    //Q4 : ONMC.general.footCare , ONMC.general.footCareNotes[0]


    if (q4 !== undefined) {
        updatedData["ONMC.general.footCare"] = ["Yes"];
        if (q4 === "Yes") {
            updatedData["ONMC.general.footCareNotesCompiled"] = "Patient reports they have routine foot examinations with a healthcare provider: " + q4Yes;
        } else if (q4 === "Not applicable") {
            updatedData["ONMC.general.footCareNotesCompiled"] = q4Notes;
        } else if (q4 === "No") {
            if (q4Ck1 === false && q4Ck2 === false) {
                q4String = "Patient reports they do not have routine foot examinations with a healthcare provider."
            } else {
                q4String = "Patient reports they do not have routine foot examinations with a healthcare provider."
                if (q4Ck1 === true) {
                    q4String += "Recommended having examinations at least annually to screen and evaluate for diabetic-related foot complications.\t"
                }
                if (q4Ck2 === true) {
                    q4String += "Patient requires a follow-up consultation to review foot care best practices.\t"
                }

            }
            updatedData["ONMC.general.footCareNotesCompiled"] = q4String;
        }
    } else {
        var notes = immutableData["ONMC.general.footCareNotes"];
        if (notes === undefined || notes === "") {
            updatedData["ONMC.general.footCare"] = undefined;
        }
        updatedData["ONMC.general.footCareNotesCompiled"] = undefined;
    }
    //q5 ONMC.general.eyeHealthNotes , ONMC.general.eyeHealth

    if (q5 !== undefined) {
        updatedData["ONMC.general.eyeHealth"] = ["Yes"];
        if (q5 === "Yes") {
            updatedData["ONMC.general.eyeHealthNotesCompiled"] = "Patient reports they have routine eye examinations with a vision care professional" + q5Yes;
        } else if (q5 === "Not Applicable") {
            updatedData["ONMC.general.eyeHealthNotesCompiled"] = q5Notes;
        } else if (q5 === "No") {
            if (q5Ck1 === false && q5Ck2 === false) {
                q5String = "Patient reports they do not have routine eye examinations with a vision care professional."
            } else {
                if (q5Ck1 === true) {
                    q5String = "Patient reports they do not have routine eye examinations with a vision care professional. Recommended having examinations at least annually to screen and evaluate for diabetic retinopathy. \n"
                }
                if (q5Ck2 === true) {
                    q5String += "Patient reports they do not have routine eye examinations with a vision care professional. Patient requires a follow-up consultation to review eye health.\n"
                }

            }
            updatedData["ONMC.general.eyeHealthNotesCompiled"] = q5String;
        }
    } else {
        var notes = immutableData["ONMC.general.eyeHealthNotes"];
        if (notes === undefined || notes === "") {
            updatedData["ONMC.general.eyeHealth"] = undefined;
        }
        updatedData["ONMC.general.eyeHealthNotesCompiled"] = undefined;
    }
    //q6 ONMC.training.sickDaysMgmtNotes , ONMC.training.sickDaysMgmt


    if (q6 !== undefined) {
        updatedData["ONMC.training.sickDaysMgmt"] = ["Yes"];
        if (q6 === "Yes") {
            updatedData["ONMC.training.sickDaysMgmtNotesCompiled"] = "Patient has a sick day management plan: " + q6Yes;
        } else if (q6 === "Not Applicable") {
            updatedData["ONMC.training.sickDaysMgmtNotesCompiled"] = q6NaNotes;
        } else if (q6 === "No") {
            if (q6Ck1 === false) {
                q6String = "Patient does not have a sick day management plan."
            } else {
                q6String = "Patient does not have a sick day management plan. Patient requires a follow-up consultation to develop a sick day management plan.  \n"

            }
            updatedData["ONMC.training.sickDaysMgmtNotesCompiled"] = q6String;
        }
    } else {
        var notes = immutableData["ONMC.training.sickDaysMgmtNotes"];
        if (notes === undefined || notes === "") {
            updatedData["ONMC.training.sickDaysMgmt"] = undefined;
        }
        updatedData["ONMC.training.sickDaysMgmtNotesCompiled"] = undefined;
    }

    //q7 ONMC.specialtyTraining.properInjectionNotes, ONMC.specialtyTraining.properInjection


    if (q7 !== undefined) {
        updatedData["ONMC.specialtyTraining.properInjection"] = ["Yes"];
        if (q7 === "Yes") {
            updatedData["ONMC.specialtyTraining.properInjectionNotesCompiled"] = "Patient an injection site rotation plan: " + q7Yes;
        } else if (q7 === "Not Applicable") {
            updatedData["ONMC.specialtyTraining.properInjectionNotesCompiled"] = q7NaNotes;
        } else if (q7 === "No") {
            if (q7Ck1 === false) {
                q7String = "Patient does not have an injection site rotation plan."
            } else {
                q7String = "Patient requires a follow-up consultation to develop an injection site rotation plan and review safe injection principles"

            }
            updatedData["ONMC.specialtyTraining.properInjectionNotesCompiled"] = q7String;
        }
    } else {
        var notes = immutableData["ONMC.specialtyTraining.properInjectionNotes"];
        if (notes === undefined || notes === "") {
            updatedData["ONMC.specialtyTraining.properInjection"] = undefined;
        }

        updatedData["ONMC.specialtyTraining.properInjectionNotesCompiled"] = undefined;
    }

} else {
    var notes = immutableData["ONMC.specialtyTraining.properInjectionNotes"];
    if (notes === undefined || notes === "") {
        updatedData["ONMC.specialtyTraining.properInjection"] = undefined;
    }
    updatedData["ONMC.specialtyTraining.properInjectionNotesCompiled"] = undefined;

    notes = immutableData["ONMC.training.sickDaysMgmtNotes"];
    if (notes === undefined || notes === "") {
        updatedData["ONMC.training.sickDaysMgmt"] = undefined;
    }
    updatedData["ONMC.training.sickDaysMgmtNotesCompiled"] = undefined;

    notes = immutableData["ONMC.general.eyeHealthNotes"];
    if (notes === undefined || notes === "") {
        updatedData["ONMC.general.eyeHealth"] = undefined;
    }
    updatedData["ONMC.general.eyeHealthNotesCompiled"] = undefined;

    notes = immutableData["ONMC.general.footCareNotes"];
    if (notes === undefined || notes === "") {
        updatedData["ONMC.general.footCare"] = undefined;
    }
    updatedData["ONMC.general.footCareNotesCompiled"] = undefined;

    notes = immutableData["ONMC.training.otherSMBGNotes"];
    if (notes === undefined || notes === "") {
        updatedData["ONMC.training.otherSMBG"] = undefined;
    }
    updatedData["ONMC.training.otherSMBGNotesCompiled"] = undefined;

    updatedData["ONMC.lifestyle.labWork"] = undefined;
    updatedData["ONMC.lifestyle.footRoutine"] = undefined;
    updatedData["ONMC.lifestyle.visionAppt"] = undefined
    updatedData["ONMC.lifestyle.sdPlan"] = undefined;
    updatedData["ONMC.lifestyle.injSit"] = undefined;

    /*updatedData["ONMC.lifestyle.labWork"] = ["No"];
    updatedData["ONMC.lifestyle.footRoutine"] = ["No"];
    updatedData["ONMC.lifestyle.visionAppt"] = ["No"];
    updatedData["ONMC.lifestyle.sdPlan"] = ["No"];
    updatedData["ONMC.lifestyle.injSit"] = ["No"];*/

}

    //Your Custom Code Ends Here
    callback(updatedData);
}

