nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var topicAbbreviations = {
    "HYPOGlycemia":"HYPOGlycemia",
    "Footcare":"Foot Care",
    "EyeHealth":"Eye Health",
    "SelfMBG":"Self-monitoring of Blood Glucose",
    "HYPERGlycemia":"HYPERGlycemia",   
    "SickDayManagement":"Sick Day Management",
    "Nutrition" : "Nutrition",
    "Vaccinations" : "Vaccinations",
    "Injections":"Injections",
    "DiabetesTargets" : "Diabetes Targets and POCT",
    "A1C":"A1C",
    //"OtherDiabetesTopic":"Other"
   };
   function sortArrayByDateDelivered(pcpArray){
    var dateBooked = [];
    var bookingPending = [];
    pcpArray.forEach( obj =>{
        if(!isNaN(Date.parse(obj.dateDelivered))){
            dateBooked.push(obj);
        }else {
            bookingPending.push(obj)
        }
    }
    );

    dateBooked.sort((a,b) => new Date(a.dateDelivered) - new Date(b.dateDelivered))
    bookingPending.sort((a,b) => a.topic.localeCompare(b.topic));
    var sortedArray = [...dateBooked , ...bookingPending];
    return sortedArray;

}


   function separateIntoThreeArrays(objInspected){
    var otherDiabetes = objInspected.filter(appt => appt["dateDelivered"] === "Cannot be tracked");
    var bookingPending = objInspected.filter(appt => appt["dateDelivered"] === "Delivery Pending" && appt["dateBooked"] === "Booking Pending");
    var dateBooked = objInspected.filter(appt => appt["dateDelivered"] === "Delivery Pending" && appt["dateBooked"] !== "Booking Pending");
    otherDiabetes.sort((a,b) => new Date(a.dateBooked) - new Date(b.dateBooked));
    bookingPending.sort((a,b) => a.topic.localeCompare(b.topic));
    dateBooked.sort((a,b) => new Date(a.dateBooked) - new Date(b.dateBooked))

    var sortedArray = [...dateBooked , ...bookingPending, ...otherDiabetes];
    return sortedArray;
}

function formatForPDF(objInCheck){
    objInCheck.forEach( appt => {
        appt.isDOABold = false;
        appt.isDODBold = false;
        appt.isDOAItalics = false;
        appt.isCompleted = false;
        if(appt.dateDelivered === "Delivery Pending"){appt.isDODBold = true;}
        if(appt.dateBooked === "Booking Pending"){appt.isDOABold = true; appt.isDOAItalics = false;}
        if(appt.dateBooked === "Not Scheduled"){appt.isDOABold = false; appt.isDOAItalics = true;}
        if(appt.status === "Delivered"){appt.isCompleted = true; appt.isDOABold = false; appt.isDODBold = false;}
    }
    )

    return objInCheck;
}

function convertTableToObject(htmlTable) {
    const table = document.createElement('table');
    table.innerHTML = htmlTable;
  
    const headers = Array.from(table.querySelectorAll('th')).map(header => header.textContent.trim());
    const rows = Array.from(table.querySelectorAll('tr')).slice(1);
    const data = rows.map(row => {
      const rowData = {};
      Array.from(row.querySelectorAll('td')).forEach((cell, index) => {
        const header = headers[index];
        if (header) {
          rowData[header] = cell.textContent.trim();
        }
      });
      return rowData;
    });
  
    return data;
}

function formatObject(objectToAssess){
    appointmentsValid = objectToAssess.filter(appt => appt["Appointment Type"] !== "No appointments have been added to Personalized Care Plan.")
    appointmentsValid.forEach (
        appointment => {
            if(appointment["Appointment Type"].includes(" - Current Session")){appointment["Appointment Type"] = appointment["Appointment Type"].replace(" - Current Session","");}
            if(appointment["Date Delivered"].includes(" (Today)")){appointment["Date Delivered"] = appointment["Date Delivered"].replace(" (Today)","");}
            
        }
    )
    return appointmentsValid;
}
  
function reNameKeys (objToUse){
    var reMapped = objToUse.map(appt => (
        {
            "topic": appt["Appointment Type"],
            "dateDelivered":appt["Date Delivered"],
            "dateBooked":appt["Date of Appointment"]
        }
    ));
    
    return reMapped;
}

function isValidDate(dateUsed){
    var dateObj = new Date(dateUsed);
    return !isNaN(dateObj.getTime());
}


//Initilize  
var htmlTable = immutableData["personalizedCarePlanHTML.diabetesFollowUp"];
var htmlTable2 = immutableData["personalizedCarePlanHTML.a1cFollowUp"];

//Create Object
var  diabetesOj = convertTableToObject(htmlTable);
var  A1COj = convertTableToObject(htmlTable2);

//Remove rows with current session/Today/No appointments have been added to Personalized Care Plan.
formatDiabetesOj = formatObject(diabetesOj);
formatA1COj = formatObject(A1COj);
console.log("formatDiabetesOj")
console.log(formatDiabetesOj)
//Change keys back to nnormal use case
formatDiabetesOj = reNameKeys(formatDiabetesOj);
formatA1COj = reNameKeys(formatA1COj);
formatDiabetesOj.forEach( appt => {
    if(isValidDate(appt["dateDelivered"])){appt.status = "Delivered"}
})

formatA1COj.forEach( appt => {
    if(isValidDate(appt["dateDelivered"])){appt.status = "Delivered"}
})

for (const [key, value] of Object.entries(topicAbbreviations)) {
    var bookStatus = immutableData["pcp." + key + "BookNow"] ? immutableData["pcp." + key + "BookNow"][0] : undefined;
    var rebookStatus = immutableData["pcp." + key + "ReBook"] ? immutableData["pcp." + key + "ReBook"][0] : undefined;
    var bookDate = immutableData["pcp." + key + "BookNowDate"] ? immutableData["pcp." + key + "BookNowDate"] : undefined;
    var rebookDate = immutableData["pcp." + key + "ReBookDate"] ? immutableData["pcp." + key + "ReBookDate"] : undefined;
    var dateBooked = "";
    var dateBookedUTC = "";
    var dateRebooked = "";
    var bookingStatus = "";
    var dateDelivered = "Delivery Pending";

    if (bookStatus === "Book Now"){
        dateBooked = bookDate ;
        dateBookedUTC = new Date(bookDate);
        bookingStatus="Book";
    }else if (bookStatus === "Book Later"){
        dateBooked = "Booking Pending"
        bookingStatus="Later";
    }

    if (rebookStatus === "Book Now/ Rebook"){
        dateBooked = rebookDate ;
        dateBookedUTC = new Date(rebookDate);
        bookingStatus="Book";
    }

    if (bookingStatus.length > 0 && value !== 'A1C') { 
        var index = formatDiabetesOj.findIndex(obj => obj.topic === value && obj.dateDelivered === "Delivery Pending");
        if (index !== -1){
            formatDiabetesOj[index].dateBooked = dateBooked;
            formatDiabetesOj[index].dateBookedUTC = dateBookedUTC;
            formatDiabetesOj[index].status = bookingStatus;

        } else {
            formatDiabetesOj.push (
                {
                    "topic" : value,
                    "dateBookedUTC": dateBookedUTC,
                    "dateBooked": dateBooked,
                    "status": bookingStatus,
                    "dateDelivered": dateDelivered                     
                   }
            );
        }
    } else if (bookingStatus.length > 0 && value === 'A1C') {
        var index = formatA1COj.findIndex(obj => obj.topic === value && obj.dateDelivered === "Delivery Pending" );
        if (index !== -1){
            formatA1COj[index].dateBooked = dateBooked;
            formatA1COj[index].dateBookedUTC = dateBookedUTC;
            formatA1COj[index].status = bookingStatus;

        } else {
            formatA1COj.push (
                {
                    "topic" : value,
                    "dateBookedUTC": dateBookedUTC,
                    "dateBooked": dateBooked,
                    "status": bookingStatus,
                    "dateDelivered": dateDelivered,
                }
            );
        }
    }

    
    
}

//Other topic 
var otherTopic = immutableData["pcp.OtherDiabetesTopicBookTopic"] ? immutableData["pcp.OtherDiabetesTopicBookTopic"] : undefined;
var otherBookNow = immutableData["pcp.OtherDiabetesTopicBookNow"] ? immutableData["pcp.OtherDiabetesTopicBookNow"][0] : undefined;
var otherBookNowDate = immutableData["pcp.OtherDiabetesTopicBookNowDate"] ? immutableData["pcp.OtherDiabetesTopicBookNowDate"] : undefined;

if (otherTopic !== undefined && otherBookNow !== undefined && otherBookNowDate !== undefined){
    formatDiabetesOj.push (
        {
            "topic" : otherTopic,
            "dateBookedUTC": new Date(otherBookNowDate),
            "dateBooked": otherBookNowDate ,
            "status": "",
            "dateDelivered": "Cannot be tracked"                     
           }
    );
}
formatDiabetesOj = formatForPDF(formatDiabetesOj);
formatA1COj = formatForPDF(formatA1COj);

//Remove completed Topics 
var completedDiabetes = formatDiabetesOj.filter(appt => appt.status === "Delivered")
var completedA1C = formatA1COj.filter(appt => appt.status === "Delivered")
completedDiabetes = sortArrayByDateDelivered(completedDiabetes);
completedA1C = sortArrayByDateDelivered(completedA1C);
//Format the arrays 
var basicDiabetes = separateIntoThreeArrays(formatDiabetesOj);
var basicA1C = separateIntoThreeArrays(formatA1COj);

formatDiabetesOj = [...completedDiabetes , ...basicDiabetes];
formatA1COj = [...completedA1C , ...basicA1C];

if (formatDiabetesOj.length > 0 ){
    updatedData["pcp.pdfDiabetesObject"] = formatDiabetesOj ; 

} else {
    updatedData["pcp.pdfDiabetesObject"] = undefined ; 

}
if (formatA1COj.length > 0){
    updatedData["pcp.pdfA1CObject"] = formatA1COj ; 

} else {
    updatedData["pcp.pdfA1CObject"] = undefined;
}



  
// Set panel time in GTM
setPanelTime("Personalized Care Plan",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

