// State variables
var dataLayer  = parent.dataLayer || []; // Initialize GTM dataLayer object
var gtmSubmit = false;
var startTimer = dataLayer[0]["gtm.start"];
var stopTimer = 0;
var currentPanel = "";
var gtmSessionIdSubmit = false;

function setSessionId (sessionId){
    if(!gtmSessionIdSubmit){
        dataLayer.push({
                    'event':'setSessionId',
                    'sessionId': sessionId
                    });
        gtmSessionIdSubmit = true;
    }
}

function setPanelTime (panelName,immutableData){
    var environment;
    if (immutableData["ENVIRONMENT.HOST"] === "sit-eforms.loblaw.ca"){
        environment = "SIT"
    }
    else if(immutableData["ENVIRONMENT.HOST"] === "eforms.loblaw.ca"){
        environment = "PROD"
    }
    console.log("## Execute setPanelTime ##");
    if(currentPanel !== panelName){
        console.log("currentPanel = " + currentPanel + ". New panel, push panel timing to dataLayer.");

        stopTimer = Date.now();
        var timeSpent = 0;
        timeSpent = Math.floor((stopTimer - startTimer) / 1000) + " seconds";
        currentPanel = panelName;

        console.log("currentPanel = " + currentPanel);
        console.log("Stop Timer = " + stopTimer);
        console.log("Time Spent = " + timeSpent);
        dataLayer.push({
                    'event':'setPanelTime',
                    'timeSpent':timeSpent,
                    'panelName':currentPanel,
                    'primaryFormName': 'on-medscheck-diabetes-annual',
                    'Environment' : environment
                    });

        startTimer = Date.now();
        console.log("Reset Timer, start = " + startTimer);
    }else {
        console.log("currentPanel = " + currentPanel + ". Same panel, don't push panel timing to dataLayer.");
    }
}
function checkForAllergies(immutableData, updatedData){

    if(immutableData["checkedAllergies"] !== "yes"){

        const reactions = [];
        for (let [key, value] of Object.entries(immutableData)) {
            // console.log("Does: " + key + " contain reaction[");
            // console.log("Check for reaction includes key output:" + key.includes("reaction["));

            if(key.includes("reaction[")){
                const x = key.substring(key.indexOf("[") + 1, key.indexOf("]"));
                const newKey = key.slice(key.indexOf("]")+2);

                let counter = reactions.length;

                for (let i=0; i < x - counter + 1; i++) {
                    console.log("pushed");
                    reactions.push({});
                }

                reactions[x][newKey] = value;

            }
        }
        let allergies = "";
        for(let i=0; i<reactions.length; i++){

            console.log("length of reactions is:" + reactions.length);

            const name = immutableData["reaction["+i+"].name"] ? immutableData["reaction["+i+"].name"] : "";
            const upcStrength = immutableData["reaction["+i+"].drug.upcStrength"] ? ", " + immutableData["reaction["+i+"].drug.upcStrength"] : "";
            const upcUnit = immutableData["reaction["+i+"].drug.upcUnit"] ? " " + immutableData["reaction["+i+"].drug.upcUnit"] : "";
            const upcManufacturer = immutableData["reaction["+i+"].drug.upcManufacturer"] ? ", " + immutableData["reaction["+i+"].drug.upcManufacturer"] : "";
            const reactionDescr = immutableData["reaction["+i+"].reactionDescr"] ? ", Reaction: " + immutableData["reaction["+i+"].reactionDescr"] : "";

            let notes = "";

            for (let [key, value] of Object.entries(reactions[i])) {
                if(key.includes("note[") && key.includes(".text")){
                    notes = notes.length > 0 ? notes + ", " + value : ", Notes: " + value;
                }
            }
            allergies = allergies + name + upcStrength + upcUnit + upcManufacturer + reactionDescr + notes + ";\n";
        } 
        updatedData["ONMC.hasAllergies"] = allergies.length > 0 ? ["yes"] : undefined;
        updatedData["ONMC.allergies"] = allergies;
    }
    updatedData["checkedAllergies"] = "yes";
}

function countDTRows(immutableData, table) {
    const obj = immutableData;
    let count = 0,
        i=0;
    for (var key in obj) {
        if (key.includes(table+'['+i)){
            count++;
            i++;
        }
    }
    return count
}

//for testing purposes
function sampleMedInfo (immutableData, updatedData) {
    for (let i=0; i<36; i++) {
        updatedData["medication[" + i + "].name"] = "Med " + i;
        updatedData["medication[" + i + "].strengthCombined"] = "strength " + i;
        updatedData["medication[" + i + "].dosage"] = "dosage " + i;
