function getTodayDate (){

    let today = new Date();

    console.log("the date generated is: " + today);

    dd = String(today.getDate()).padStart(2, '0');
    mm = String(today.getMonth() + 1).padStart(2, '0');
    yyyy = today.getFullYear();

    today = yyyy + '-' + mm + '-' + dd;

    try{log("Today's date is: " + today);}
        catch (err) {}

    return today;
}
function correctData (immutableData,updatedData,incorrectBinding,correctBinding){
    var data= immutableData[incorrectBinding];
    if(immutableData[incorrectBinding] !== undefined ){
        updatedData[correctBinding] = [data] ;
        updatedData[incorrectBinding] = undefined;
    }
}

function calcAge(dateString) {
  var birthday = +new Date(dateString);
  return ~~((Date.now() - birthday) / (31557600000));
}

