  function getFormattedDate(date, format) {
    var year = date.getFullYear();
    var month = (1 + date.getMonth()).toString();
    month = month.length > 1 ? month : '0' + month;
    var day = date.getDate().toString();
    day = day.length > 1 ? day : '0' + day;
    if (format == 'mm-dd-yyyy') {
        return month + '-' + day + '-' + year;
    }
    else if (format == 'yy-mm-dd') {
      year = year.toString().substr(-2);
        return year + '-' + month + '-' + day;
    }
    else if (format == 'mm/dd/yyyy') {
        return month + '/' + day + '/' + year;
    }
    else if (format == 'yy/mm/dd') {
      year = year.toString().substr(-2);
        return year + '/' + month + '/' + day;
    }
    else {
        return year + '-' + month + '-' + day;
    }
  }
function defineEsignature(updatedData, immutableData) {
    if (immutableData['digitalSignature'] === "1") {
        var dateObj = new Date();
        var currentDate = getFormattedDate(dateObj, 'yyyy-mm-dd');
        var currentTime = dateObj.getHours() + ":" + (dateObj.getMinutes() < 10 ? '0' : '') + dateObj.getMinutes();
        updatedData['esignature'] = "Electronically authorized by " + immutableData['user.App.UserID'] + " on " + currentDate + ", " + currentTime;
    }
}



function configurePCPHtmlBlock(currentPCP,pastPCP,hasA1C,deliveryDateProvided){
    var htmlBlock = '<table><tr><th>Appointment Type</th><th>Date of Appointment</th><th>Date Delivered </th></tr>';
    var totalRows = 0 ;
    pastPCP.forEach(event => {
        var topic = event.topic;
        var deliveryDate = event.dateDelivered;
        var dateOfAppointment = event.dateBooked === "Not Scheduled" ? '<span class="italicFont">Not Scheduled</span>' : event.dateBooked;
        totalRows++;
        var htmlString = `<tr class="grey"><td>${topic}</td><td>${dateOfAppointment}</td><td>${deliveryDate}</td></tr>`
        htmlBlock+= htmlString;
    });


    if (hasA1C === true){
        totalRows++;
        var today = getTodayDate();
        var index = currentPCP.findIndex(obj => obj.topic === 'A1C' );
        if (index !== -1){
            var removedObject = currentPCP.splice(index,1)[0];
            removedObject.dateDelivered = deliveryDateProvided;
            //removedObject.dateBooked = today;
            var topic = `<tr class="currentSession"><td>${removedObject.topic} - <span>Current Session</span></td>`;
            var deliveryDate = removedObject.dateDelivered;
            if (today === deliveryDateProvided ) {deliveryDate = today + " (Today)";}
            var dateOfAppointment = removedObject.dateBooked === "Booking Pending" ? '<span class="italicFont">Not Scheduled</span>' : removedObject.dateBooked ;
            var htmlString = `${topic}</td><td>${dateOfAppointment}</td><td>${deliveryDate}</td></tr>`
            htmlBlock+= htmlString;
        }else {
            var deliveryDate = deliveryDateProvided;
            if (today === deliveryDateProvided ) {deliveryDate = today + " (Today)";} 
            var htmlString = `<tr class="currentSession"><td>A1C - <span>Current Session</span></td><td><span class="italicFont">Not Scheduled</span></td><td>${deliveryDate}</td></tr>`
            htmlBlock+= htmlString;
        }
    }

    currentPCP.forEach(event => {
        totalRows++;
        var topic = `<tr><td>${event.topic}</td>`;
        var deliveryDate = event.dateDelivered === "Delivery Pending" ? '<span class="textBolded">Delivery Pending</span>' : event.dateDelivered;
        var dateOfAppointment = event.dateBooked === "Booking Pending" ? '<span class="textBolded">Booking Pending</span>' : event.dateBooked;
        var htmlString = `${topic}</td><td>${dateOfAppointment}</td><td>${deliveryDate}</td></tr>`
        htmlBlock+= htmlString;
    });

    if (totalRows === 0){
        var htmlString = `<tr><td colspan="3">No appointments have been added to Personalized Care Plan.</td></tr>`
        htmlBlock+= htmlString;
    }

    htmlBlock += '</table>'

    return htmlBlock;
}

function configureACResults(currentResults,newResult){
    var finalObj = currentResults;
    var htmlBlock = '<table><tr><th>Date of Test</th><th>A1C Value</th></tr>';
    var totalRows = 0 ;
    console.log("Check1 for session in config html block")
    if (finalObj !== undefined){
    finalObj.forEach(event => {
        var result = event.testResult.toString(); 
        if(result.slice(-1) !== "%"){result = event.testResult += "%";}
        var htmlString = `<tr><td>${event.dateDelivered}</td><td>${result}</td></tr>`;
        totalRows++;
        htmlBlock+= htmlString;
    });
    }
    console.log("Check for current session in config html block")
    if (newResult !== undefined){
        var result = newResult["testResult"].toString() ; 
        if (getTodayDate() === newResult['dateDelivered'] ) {newResult['dateDelivered'] += " (Today)";} 
        if(result.slice(-1) !== "%"){result = newResult["testResult"] += "%";}
        var htmlString = `<tr><td>${newResult['dateDelivered']}</td><td>${result}</td></tr>`;
        totalRows++;
        htmlBlock+= htmlString;

    }
    if (totalRows === 0){
        var htmlString = `<tr><td colspan="2">A1C values are not available.</td></tr>`
        htmlBlock+= htmlString;
    }
    htmlBlock += '</table>'

    return htmlBlock;
}
