function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    var faxSummary = []
    //setup fax integration
    if (immutableData['store.smartFormAutoFax.enabled'] == 'yes') {
        if (immutableData['autofax1'] == 'Yes') {
            var fax1Files = []
            var fax1List = [
                ['faxhcp1', 'HealthCare Provider Notification 1.pdf'],
                ['faxworksheet1', 'Pharmacist Worksheet.pdf'],
                ['patientRecord1', 'Personal Medication Record.pdf'],
            ]
            var fax1Files = fax1List.filter(([key]) => immutableData[key]).map(([,value]) => value)
            if (fax1Files.length > 0) {
              faxSummary.push({
                prescriberFirstName: immutableData['ONMC.followup.firstName1'],
                prescriberLastName: immutableData['ONMC.followup.lastName1'],
                phoneNumber: immutableData['ONMC.followup.telephoneNumber1'] ? immutableData['ONMC.followup.telephoneNumber1'].replace(/\D/g, '') : null,
                faxNumber: immutableData['ONMC.followup.fax1'] ? immutableData['ONMC.followup.fax1'].replace(/\D/g, '') : null,
                fileNames: fax1Files,
              })
            }
        }
        if (immutableData['autofax2'] == 'Yes') {
            var fax2Files = []
            var fax2List = [
              ['faxhcp2', 'HealthCare Provider Notification 2.pdf'],
              ['faxworksheet2', 'Pharmacist Worksheet.pdf'],
              ['patientRecord2', 'Personal Medication Record.pdf'],
            ]
            var fax2Files = fax2List.filter(([key]) => immutableData[key]).map(([,value]) => value)
            if (fax2Files.length > 0) {
              faxSummary.push({
                prescriberFirstName: immutableData['ONMC.followup.firstName2'],
                prescriberLastName: immutableData['ONMC.followup.lastName2'],
                phoneNumber: immutableData['ONMC.followup.telephoneNumber2'] ? immutableData['ONMC.followup.telephoneNumber2'].replace(/\D/g, '') : null,
                faxNumber: immutableData['ONMC.followup.fax2'] ? immutableData['ONMC.followup.fax2'].replace(/\D/g, '') : null,
                fileNames: fax2Files,
              })
            }
          }
    }
    
    if(faxSummary.length > 0){
        updatedData['faxSummary'] = faxSummary;
    } else {
        updatedData['faxSummary'] = null;
    }
    
    
    const additionalInfoChoice = immutableData["additionalInfo"] ? immutableData["additionalInfo"][0] : undefined;
    
    //backtracking in case user switches answer from yes to no, remove the text
   if(additionalInfoChoice === "No"){
        updatedData["additionalInfoText"] = undefined;
        updatedData["additionalInfoTextForPDF"] = undefined;
    } else {
        var norm = immutableData["additionalInfoText"] ? immutableData["additionalInfoText"] : "";
        var comp = immutableData["additionalInfoTextCompiled"] ? immutableData["additionalInfoTextCompiled"] + "\n" : "";
        
         updatedData["additionalInfoTextForPDF"] = comp + norm;
    }
    
    
    
    
//FORMAT - PDF FileName , alreadyGaveConsent[yes,no], AgentCheck[true,false],generateHCP[true,false],additionalInfo[yes,no],serviceSelected, expectedValue - default false , js will change to true

pdfCombinations = [
    ["annualPackage1","Yes",false,true,"No","Annual",undefined],
    ["annualPackage2","No",false,true,"No","Annual",undefined],
    ["annualPackage3","No",false,true,"Yes","Annual",undefined],
    ["annualPackage4","No",true,true,"Yes","Annual",undefined],
    ["annualPackage5","No",true,true,"No","Annual",undefined],
    ["annualPackage6","Yes",false,true,"Yes","Annual",undefined],
    ["diabetesFullPackage1","Yes",false,true,"No","Diabetes",undefined],
    ["diabetesFullPackage2","No",false,true,"No","Diabetes",undefined],
    ["diabetesFullPackage3","No",false,true,"Yes","Diabetes",undefined],
    ["diabetesFullPackage4","No",true,true,"Yes","Diabetes",undefined],
    ["diabetesFullPackage5","No",true,true,"No","Diabetes",undefined],
    ["diabetesFullPackage6","Yes",false,true,"Yes","Diabetes",undefined],
    ["diabetesCondensedPackage1","Yes",false,false,"No","Diabetes",undefined],
    ["diabetesCondensedPackage2","No",false,false,"No","Diabetes",undefined],
    ["diabetesCondensedPackage3","No",false,false,"Yes","Diabetes",undefined],
    ["diabetesCondensedPackage4","No",true,false,"Yes","Diabetes",undefined],
    ["diabetesCondensedPackage5","No",true,false,"No","Diabetes",undefined],
    ["diabetesCondensedPackage6","Yes",false,false,"Yes","Diabetes",undefined],
]

var agentCheck = immutableData["ONMC.patient.agentCheck"] ? immutableData["ONMC.patient.agentCheck"] : false;
var generateHCP = immutableData["generateHCPPDF"] ? immutableData["generateHCPPDF"] : false;
var additionalInfo = immutableData["additionalInfo"] ? immutableData["additionalInfo"][0] : "No";
var serviceSelected = immutableData["ONMC.service"] ? immutableData["ONMC.service"][0] : undefined;
var selectedPackage=0
for (x=0 ; x < pdfCombinations.length; x++)
{
    if (immutableData["LOGIC.alreadyGaveConsent"][0] === pdfCombinations[x][1] && 
    agentCheck === pdfCombinations[x][2]  && 
    additionalInfo == pdfCombinations[x][4] && 
    serviceSelected === pdfCombinations[x][5] &&
    generateHCP === pdfCombinations[x][3]  ){
        pdfCombinations[x][6] = true
        selectedPackage=x;
    }
 
}

for (x=0 ; x < pdfCombinations.length; x++)
{
   val = pdfCombinations[x][0];
   updatedData[val] = pdfCombinations[x][6];
 
}
if (selectedPackage > 0 && immutableData["ENVIRONMENT.HOST"] == "sit-eforms.loblaw.ca") {
    console.log("Package Selected\t" + pdfCombinations[selectedPackage][0])
}else if(immutableData["ENVIRONMENT.HOST"] == "sit-eforms.loblaw.ca"){
    console.log("----THIS IS A NEW PACKAGE------")//ADD ELSE STATEMENTED TO CONSOLE LOG NEW CONBINATION 
    console.log("Gave consent: " + immutableData["LOGIC.alreadyGaveConsent"][0]);
    console.log("Agent Check: " + agentCheck);
    console.log("Additional Information: " + additionalInfo);
    console.log("Generate HCP: " + generateHCP);
    console.log("Service: " + serviceSelected);
}
var text = immutableData["pharmacy.fax"];
if (text !== undefined){
  var removeSpaces = text.split(' ').join('');
    var removeDash = removeSpaces.split('-').join('');
    var removeOpenBracket = removeDash.split('(').join('');
    var removeCloseBracket = removeOpenBracket.split(')').join('');
    updatedData["pharmacy.faxAreaCode"] = removeCloseBracket[0] + removeCloseBracket[1] + removeCloseBracket[2] ; 
    updatedData["pharmacy.faxDigits1"] = removeCloseBracket[3] + removeCloseBracket[4] + removeCloseBracket[5] ; 
    updatedData["pharmacy.faxDigits2"] = removeCloseBracket[6] + removeCloseBracket[7] + removeCloseBracket[8] + removeCloseBracket[9]; 
} else {
    updatedData["pharmacy.faxAreaCode"] = undefined ; 
    updatedData["pharmacy.faxDigits1"] = undefined ; 
    updatedData["pharmacy.faxDigits2"] = undefined ; 
}  

defineEsignature(updatedData, immutableData);

// Set panel time in GTM
    setPanelTime("Additional Information",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    function quickPrintPageNumbers(immutableData, updatedData){
    
    let numMeds = immutableData["quickPrint.meds"];
    let requiredPages = 0;
    let requiredPagesPMR = 0;

    // PHARMACIST WORKSHEET PAGE NUMBERS - 6 rows per page
    requiredPages = Math.ceil(numMeds / 6);
    // set the dynamic page numbers
    updatedData["ONMC.pageNumber.one"] = "Page 1 of " + (requiredPages + 3);
    updatedData["ONMC.pageNumber.three"] = "Page 2 of " + (requiredPages + 3);
    updatedData["ONMC.pageNumber.three"] = numMeds >= 0 ? "Page 3 of " + (requiredPages + 3) : undefined;
    updatedData["ONMC.pageNumber.four"] = numMeds >= 7 ? "Page 4 of " + (requiredPages + 3) : undefined;
    updatedData["ONMC.pageNumber.five"] = numMeds >= 13 ? "Page 5 of " + (requiredPages + 3) : undefined;
    updatedData["ONMC.pageNumber.six"] = numMeds >= 19 ? "Page 6 of " + (requiredPages + 3) : undefined;
    updatedData["ONMC.pageNumber.seven"] = numMeds >= 25 ? "Page 7 of " + (requiredPages + 3) : undefined;
    updatedData["ONMC.pageNumber.eight"] = numMeds >= 31 ? "Page 8 of " + (requiredPages + 3) : undefined;
    updatedData["ONMC.pageNumber.nine"] = "Page " + (requiredPages + 3) + " of " + (requiredPages + 3);

    // PERSONAL MEDICATION RECORD PAGE NUMBERS - 6 rows per page, the first page has 2 rows
    requiredPagesPMR = Math.ceil((numMeds - 2) / 6);
    // set the dynamic page numbers
    updatedData["ONMC.pageNumberPMR.one"] = "Page 1 of " + (requiredPagesPMR + 1);
    updatedData["ONMC.pageNumberPMR.two"] = numMeds >= 3 ? "Page 2 of " + (requiredPagesPMR + 1) : undefined;
    updatedData["ONMC.pageNumberPMR.three"] = numMeds >= 9 ? "Page 3 of " + (requiredPagesPMR + 1) : undefined;
    updatedData["ONMC.pageNumberPMR.four"] = numMeds >= 15 ? "Page 4 of " + (requiredPagesPMR + 1) : undefined;
    updatedData["ONMC.pageNumberPMR.five"] = numMeds >= 21 ? "Page 5 of " + (requiredPagesPMR + 1) : undefined;
    updatedData["ONMC.pageNumberPMR.six"] = numMeds >= 27 ? "Page 6 of " + (requiredPagesPMR + 1) : undefined;
    updatedData["ONMC.pageNumberPMR.seven"] = numMeds >= 33 ? "Page 7 of " + (requiredPagesPMR + 1) : undefined;
}
    
    if (immutableData["quickPrint"][0] === "Yes") {
        // call the above quickPrintPageNumbers function
        quickPrintPageNumbers(immutableData, updatedData);
        
        // create new databinding called "quickPrintActivated" for HW purposes (for resume functionality via HW)
        updatedData["quickPrintActivated"] = true;
        
        // set tobacco, recDrugs, and alcohol bindings based on whether or not user has interacted with them - if not, set to "No value entered", so we know that we need to prompt user later to fill it in
        // Lifestyle panel fields
        updatedData["ONMC.lifestyle.tobacco"] = immutableData["ONMC.lifestyle.tobacco"] ? immutableData["ONMC.lifestyle.tobacco"] : ["No value entered"];
        updatedData["ONMC.lifestyle.recDrugs"] = immutableData["ONMC.lifestyle.recDrugs"] ? immutableData["ONMC.lifestyle.recDrugs"] : ["No value entered"];
        updatedData["ONMC.lifestyle.alcohol"] = immutableData["ONMC.lifestyle.alcohol"] ? immutableData["ONMC.lifestyle.alcohol"] : ["No value entered"];
        // Follow-Up panel field
        updatedData["ONMC.followup.healthcareProviders"] = immutableData["ONMC.followup.healthcareProviders"] ? immutableData["ONMC.followup.healthcareProviders"] : ["No value entered"];
        // Additional Info panel
        updatedData["additionalInfo"] = immutableData["additionalInfo"] ? immutableData["additionalInfo"] : ["No value entered"];
        // Allergies panel
        updatedData["ONMC.hasAllergies"] = immutableData["ONMC.hasAllergies"] ? immutableData["ONMC.hasAllergies"] : ["No value entered"];
        // Diabetes - General Info panel
        updatedData["ONMC.general.insulinUse"] = immutableData["ONMC.general.insulinUse"] ? immutableData["ONMC.general.insulinUse"] : ["No value entered"];
        updatedData["ONMC.general.hypoG"] = immutableData["ONMC.general.hypoG"] ? immutableData["ONMC.general.hypoG"] : ["No value entered"];
        // Primary Caregiver panel
        updatedData["LOGIC.hasCaregiver"] = immutableData["LOGIC.hasCaregiver"] ? immutableData["LOGIC.hasCaregiver"] : ["No value entered"];
        
    // backtracking logic for follow-up healthcare provider field if value is set to "No" or "No value entered"
    const followUpHealthcareProviders = immutableData["ONMC.followup.healthcareProviders"] ? immutableData["ONMC.followup.healthcareProviders"][0] : undefined;
    
    if (followUpHealthcareProviders === "No" || followUpHealthcareProviders === "No value entered") {
        updatedData["ONMC.diabetes.referralNames"] = undefined;
        updatedData["ONMC.diabetes.referral1"] = undefined;
        updatedData["ONMC.followup.firstName1"] = undefined;
        updatedData["ONMC.followup.lastName1"] = undefined;
        updatedData["ONMC.followup.type1"] = undefined;
        updatedData["ONMC.followup.telephoneNumber1"] = undefined;
        updatedData["ONMC.followup.email1"] = undefined;
        updatedData["ONMC.followup.fax1"] = undefined;
        updatedData["ONMC.diabetes.referralReason1"] = undefined;
    }
        
    } else if (immutableData["quickPrint"][0] === "No") {
        updatedData["quickPrintActivated"] = false;
        
        // if value exists and is "No value entered", set to undefined, which will mean user will need to go back and Review Missed question 
        // Lifestyle panel fields
        updatedData["ONMC.lifestyle.tobacco"] = immutableData["ONMC.lifestyle.tobacco"] && immutableData["ONMC.lifestyle.tobacco"][0] !== "No value entered" ? immutableData["ONMC.lifestyle.tobacco"] : undefined;
        updatedData["ONMC.lifestyle.recDrugs"] = immutableData["ONMC.lifestyle.recDrugs"] && immutableData["ONMC.lifestyle.recDrugs"][0] !== "No value entered" ? immutableData["ONMC.lifestyle.recDrugs"] : undefined;
        updatedData["ONMC.lifestyle.alcohol"] = immutableData["ONMC.lifestyle.alcohol"] && immutableData["ONMC.lifestyle.alcohol"][0] !== "No value entered" ? immutableData["ONMC.lifestyle.alcohol"] : undefined;
        // Follow-up panel field
        updatedData["ONMC.followup.healthcareProviders"] = immutableData["ONMC.followup.healthcareProviders"] && immutableData["ONMC.followup.healthcareProviders"][0] !== "No value entered" ? immutableData["ONMC.followup.healthcareProviders"] : undefined;
        // Additional Info panel 
        updatedData["additionalInfo"] = immutableData["additionalInfo"] && immutableData["additionalInfo"][0] !== "No value entered" ? immutableData["additionalInfo"] : undefined;
        // Allergies panel
        updatedData["ONMC.hasAllergies"] = immutableData["ONMC.hasAllergies"] && immutableData["ONMC.hasAllergies"][0] !== "No value entered" ? immutableData["ONMC.hasAllergies"] : undefined;
        // Diabetes - General Info panel
        updatedData["ONMC.general.insulinUse"] = immutableData["ONMC.general.insulinUse"] && immutableData["ONMC.general.insulinUse"][0] !== "No value entered" ? immutableData["ONMC.general.insulinUse"] : undefined;
        updatedData["ONMC.general.hypoG"] = immutableData["ONMC.general.hypoG"] && immutableData["ONMC.general.hypoG"][0] !== "No value entered" ? immutableData["ONMC.general.hypoG"] : undefined;
        // Primary Caregiver panel
        updatedData["LOGIC.hasCaregiver"] = immutableData["LOGIC.hasCaregiver"] && immutableData["LOGIC.hasCaregiver"][0] !== "No value entered" ? immutableData["LOGIC.hasCaregiver"] : undefined;
    }
    
        
    // Set panel time in GTM
    setPanelTime("Quick Print",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    //Add code for: 
    
    //1. Ensure patient diet and dietary concerns checkboxes are checked if there is text within their fields - DONE
    //2. Ensure alcohol/drugs checkbox is checked if either alcohol or drugs is selected in form. The notes should be concatendated into a single databinding for that field. - DONE
    //3. Ensure smoking cessation offered is checked if there is data in that field. - DONE
    //4. Appropriate backtracking for all fields + diabetes check - DONE
    //5. Diabetes Lifestyle checkbox checked if diet, stress reduction, or exercise are checked - DONE
    
    
    //backtracking in case user switches answer from smoking yes to no, remove the text
    const tobaccoChoice = immutableData["ONMC.lifestyle.tobacco"] ? immutableData["ONMC.lifestyle.tobacco"][0] : undefined;
    const recDrugsChoice = immutableData["ONMC.lifestyle.recDrugs"] ? immutableData["ONMC.lifestyle.recDrugs"][0] : undefined;
    const alcoholChoice = immutableData["ONMC.lifestyle.alcohol"] ? immutableData["ONMC.lifestyle.alcohol"][0] : undefined;
    var smokindCeesNotes = immutableData["ONMC.lifestyle.tobaccoCessationOfferedNotes"]; 
    var prevSmk = immutableData["ONMC.lifestyle.smokingCessation"];
    if(tobaccoChoice === "Yes, currently"){
       updatedData["ONMC.lifestyle.smokingCessation"] = undefined;
       updatedData["ONMC.lifestyle.smokingCessationCheck"] = undefined;
       // updatedData["ONMC.lifestyle.tobacco"]=true;
         updatedData["ONMC.lifestyle.tobaccoNo"]=false;
          updatedData["ONMC.lifestyle.tobaccoYes"]=true;
          updatedData["ONMC.lifestyle.tobaccoNumberPDF"]= immutableData["ONMC.lifestyle.tobaccoNumber"];
            updatedData["ONMC.lifestyle.tobaccoPreviousText"]=undefined;
            if(smokindCeesNotes !== undefined){
              updatedData["ONMC.lifestyle.smokingNotes"] = smokindCeesNotes ;
              updatedData["ONMC.lifestyle.hasSmoking"]=true;
            } else {
                 updatedData["ONMC.lifestyle.hasSmoking"]=false;
            }
    }
    
    if(tobaccoChoice === "Yes, previously"){
        updatedData["ONMC.lifestyle.tobaccoNumber"] = undefined;
        updatedData["ONMC.lifestyle.tobaccoCessationOffered"] = undefined;
        updatedData["ONMC.lifestyle.tobaccoCessationOfferedNotes"] = undefined;
         //updatedData["ONMC.lifestyle.tobacco"]=true;
          updatedData["ONMC.lifestyle.tobaccoNo"]=true;
          updatedData["ONMC.lifestyle.tobaccoYes"]=false;
          if(prevSmk !== undefined){
              updatedData["ONMC.lifestyle.smokingNotes"] = prevSmk ;
              updatedData["ONMC.lifestyle.hasSmoking"]=true;
         
            }else {
                updatedData["ONMC.lifestyle.hasSmoking"]=false;
                 updatedData["ONMC.lifestyle.smokingNotes"] = undefined;
            }
                
          updatedData["ONMC.lifestyle.tobaccoPreviousText"]="Previous Tobacco User";
          updatedData["ONMC.lifestyle.tobaccoNumberPDF"] = "Previous Tobacco User";
    }
    
    if(tobaccoChoice === "No"){
        updatedData["ONMC.lifestyle.tobaccoNumber"] = undefined;
       updatedData["ONMC.lifestyle.smokingCessation"] = undefined;
        updatedData["ONMC.lifestyle.tobaccoNumberPDF"]=undefined;
       updatedData["ONMC.lifestyle.smokingCessationCheck"] = undefined;
       updatedData["ONMC.lifestyle.tobaccoCessationOffered"] = undefined;
       updatedData["ONMC.lifestyle.smokingNotes"] = undefined ;
       updatedData["ONMC.lifestyle.tobaccoCessationOfferedNotes"] = undefined;
      //  updatedData["ONMC.lifestyle.tobacco"]=true;
        updatedData["ONMC.lifestyle.tobaccoNo"]=true;
        updatedData["ONMC.lifestyle.tobaccoPreviousText"]=undefined;
        updatedData["ONMC.lifestyle.tobaccoYes"]=false;
         updatedData["ONMC.lifestyle.hasSmoking"]=false;
         updatedData["ONMC.lifestyle.smokingNotes"]=undefined;
    }

    //Backtracking for recreational drugs 
    if(recDrugsChoice === "No"){
        updatedData["ONMC.lifestyle.recDrugsFrequency"] = undefined;
    }
    
    //Backtracking for alcohol
    if(alcoholChoice === "No"){
        updatedData["ONMC.lifestyle.alcoholFrequency"] = undefined;
    }
    
    
    //Function that checks if there is a length in a field, and replaces the 'check' binding with 'yes'
    function checkForLength(immutableData, updatedData, binding, checkBinding) {
        const field = immutableData[binding] ? immutableData[binding] : null;
        let checked = "no";
        if(field !== null){
            if(field.replace(/\s/g, '').length > 0){
                checked = "yes";
            }
        }
        updatedData[checkBinding] = checked;
    }
    
    //Running the function for fields that require a check if there is contents in the text field
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.smokingCessation", "ONMC.lifestyle.smokingCessationCheck");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.exerciseRegimen", "ONMC.lifestyle.exerciseRegimenCheck");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.exerciseActivityNotes", "ONMC.lifestyle.exerciseActivity");
    checkForLength(immutableData, updatedData, "ONMC.general.lifestyleDietNotes", "ONMC.general.lifestyleDiet");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.dietaryConcernsNotes", "ONMC.lifestyle.dietaryConcerns");
    checkForLength(immutableData, updatedData, "ONMC.lifestyle.tobaccoCessationOfferedNotes", "ONMC.lifestyle.tobaccoCessationOffered");
    
    // Backtracking for if patientDietCheck (form checkbox) is false, then patientDiet, patientDietNotes, patientConcerns, patientConcernNotes all need to be undefined
    if(immutableData["ONMC.lifestyle.patientDietCheck"] === false) {
        updatedData["ONMC.general.lifestyleDietNotes"] = undefined;
        updatedData["ONMC.general.lifestyleDiet"] = undefined;
        updatedData["ONMC.lifestyle.dietaryConcerns"] = undefined;
        updatedData["ONMC.lifestyle.dietaryConcernsNotes"] = undefined;
    }
    
    // Backtracking for if exerciseOverallCheck (form checkbox) is false, exercise, exerciseCheck, physical, phsyicalCheck undefined as well 
    if(immutableData["ONMC.lifestyle.exerciseOverallCheck"] === false) {
        updatedData["ONMC.lifestyle.exerciseRegimen"] = undefined;
        updatedData["ONMC.lifestyle.exerciseRegimenCheck"] = undefined;
        updatedData["ONMC.lifestyle.exerciseActivity"] = undefined;
        updatedData["ONMC.lifestyle.exerciseActivityNotes"] = undefined;
    }
    
    // Generates a 'yes' for the lifestyle checkbox in diabetes form if there is any 1 of 3 lifestyle questions 
    
    if (immutableData["ONMC.lifestyle.exerciseRegimenCheck"] === "yes" || immutableData["ONMC.general.lifestyleDiet"] === "yes" || immutableData["ONMC.general.lifestyleStress"] === true ) {
        updatedData["ONMC.general.lifestyleCheck"] = "yes";
    } else {
        updatedData["ONMC.general.lifestyleCheck"] = undefined;
    }
    
    // backtracking for stress, other, and notes
    if(immutableData["ONMC.general.lifestyleStress"] === false){
        updatedData["ONMC.general.lifestyleStressNotes"] = undefined;
    }
    
    if(immutableData["ONMC.lifestyle.otherCheck"] === false){
        updatedData["ONMC.lifestyle.other"] = undefined;
    }
    
    if(immutableData["ONMC.lifestyle.notesCheck"] === false){
        updatedData["ONMC.lifestyle.notes"] = undefined;
    }

    // Alcohol + Recreational Drugs concatenation for diabetes form + backtracking
    
    if (alcoholChoice === "Yes" && recDrugsChoice === "Yes"){
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = "yes";
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = "A: " + immutableData["ONMC.lifestyle.alcoholFrequency"] + " RD: " + immutableData["ONMC.lifestyle.recDrugsFrequency"];
    }else if (alcoholChoice === "Yes" && recDrugsChoice === "No") {
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = "yes";
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = "A: " + immutableData["ONMC.lifestyle.alcoholFrequency"];
    } else if (alcoholChoice === "No" && recDrugsChoice === "Yes") {
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = "yes";
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = "RD: " + immutableData["ONMC.lifestyle.recDrugsFrequency"];
    } else {
        updatedData["ONMC.lifestyle.alcoholDrugsUse"] = undefined;
        updatedData["ONMC.lifestyle.alcoholDrugsUseNotes"] = undefined; 
    }
    
    // Set panel time in GTM
    setPanelTime("Lifestyle Information",immutableData);

    //Your Custom Code Ends Here
    callback(updatedData);
}

