        updatedData["ONMC.followup.fax2"] = undefined;
        updatedData["ONMC.diabetes.referralReason2"] = undefined;
    }
    var originalString = immutableData["ONMC.allergies"];
    




    // Set panel time in GTM
    setPanelTime("Healthcare Provider Notification",immutableData);
    
    //second code block for supporting backtracking with selection changes for HWF-5461
       if(immutableData["ONMC.therapeutic.useCompliancePack"] && immutableData["ONMC.therapeutic.useCompliancePack"][0] == "Yes"){
           /* console.log("in the secondary code for 5461");
