            additionalInfoText = additionalInfoText.replaceAll("Patient reports difficulty opening medication vials.", "");
            additionalInfoText = additionalInfoText.replaceAll("Patient reports unitended mix-ups when taking medications.", "");
            additionalInfoText = additionalInfoText.replaceAll("Patient reports vials of unused or expired medications at home.", "");
            updatedData["additionalInfoText"] = additionalInfoText;
            console.log(additionalInfoText);*/
            updatedData["ONMC.clinicalNeed.refills"] = [];
            updatedData["ONMC.clinicalNeed.refillsNotes"] = "";
            updatedData["ONMC.clinicalNeed.nonAdherence"] = [];
            updatedData["ONMC.clinicalNeed.nonAdherenceNotes"] = "";
        }
        //Remove as the JS was faiil (replace all)

    //Your Custom Code Ends Here
    callback(updatedData);
}

