nodes.customOnChangeJavaScript:
function onDataChange(immutableData, callback) {
    //Add values to updatedData to modify the map of collected data.
    //    Example: updatedData['applicant.givenName'] = 'Ryan'; would set the variable 'applicant.givenName' to 'Ryan'
    var updatedData = {};
    //Your Custom Code Starts Here
    const newLineRegex = /\n/gm;
// Question 3 - BP
var bpInformation = "";
if (immutableData["ONMC.general.UnknownBP"]){
      updatedData["ONMC.general.PCStationBP"] = false ;
     updatedData["ONMC.general.OtherSourceBP"] = false;
     bpInformation = "Patient's blood pressure values are unknowns \n\n";
} else {
    if (immutableData["ONMC.general.PCStationBP"]){
         var pcSys = immutableData["ONMC.general.PCStationSysBP"] ? immutableData["ONMC.general.PCStationSysBP"]  : "";
       var pcDia = immutableData["ONMC.general.PCStationDiaBP"] ? immutableData["ONMC.general.PCStationDiaBP"]  : "";
          var pcDate = immutableData["ONMC.general.PCStationDateValueBP"] ? ", Date: " +  immutableData["ONMC.general.PCStationDateValueBP"]  : "";
     if (immutableData["ONMC.general.PCStationDateBP"] === false){pcDate = ""}
     console.log(pcDate)
     bpInformation = bpInformation + "Blood Pressure (PC Health Station) - " + pcSys +"/" + pcDia + " mmHG" + pcDate + "\n";
   }
   if (immutableData["ONMC.general.OtherSourceBP"]){
       var osSys = immutableData["ONMC.general.OtherSourceSysBP"] ? immutableData["ONMC.general.OtherSourceSysBP"]  : "";
       var osDia = immutableData["ONMC.general.OtherSourceDiaBP"] ? immutableData["ONMC.general.OtherSourceDiaBP"]  : "";
       var osDate = immutableData["ONMC.general.OtherSourceDateValueBP"] ? ", Date: " + immutableData["ONMC.general.OtherSourceDateValueBP"]  : "";
       if (immutableData["ONMC.general.OtherSourceDateBP"] === false){osDate = ""}
     bpInformation = bpInformation + "Blood Pressure (Other Source) - " + osSys +"/" + osDia + " mmHG" + osDate + "\n";
   }
   if (bpInformation.length > 0){
       bpInformation = bpInformation + "\n";
   }
    
}

 // Question 4 - BMI
var bmiInfo = "";
if (immutableData["ONMC.general.UnknownBMI"]){
     bmiInfo = "Patient's BMI values are unknowns \n\n";
     updatedData["ONMC.general.PCStationBMI"] = false ;
     updatedData["ONMC.general.OtherSourceBMI"] = false;
} else {
    if (immutableData["ONMC.general.PCStationBMI"]){
        var pcVal = immutableData["ONMC.general.PCStationDateValueBMI"] ?", Date: "+ immutableData["ONMC.general.PCStationDateValueBMI"]  + "\n": "";
       if (immutableData["ONMC.general.PCStationDateBMI"] === false){pcVal = ""}
       var weight = immutableData["ONMC.general.PCStationWeightBMI"] ? "Weight (PC Health Station) - " + immutableData["ONMC.general.PCStationWeightBMI"] + "kg"+ pcVal + "\n" : "";
       var height = immutableData["ONMC.general.PCStationHeightBMI"] ?"Height (PC Health Station) - "+ immutableData["ONMC.general.PCStationHeightBMI"] + "cm"+ pcVal + "\n" : "";
       var bmi = immutableData["ONMC.general.PCStationValueBMI"] ?"BMI (PC Health Station) - "+ immutableData["ONMC.general.PCStationValueBMI"] + pcVal + "\n"  : "";
      
     bmiInfo = bmiInfo + weight + height + bmi ;
   }
   if (immutableData["ONMC.general.OtherSourceBMI"]){
       var osVal = immutableData["ONMC.general.OtherSourceDateValueBMI"] ?", Date: "+ immutableData["ONMC.general.OtherSourceDateValueBMI"] : "";
       if (immutableData["ONMC.general.OtherSourceDateBMI"] === false ){osVal =""}
       var weight = immutableData["ONMC.general.OtherSourceWeightBMI"] ? "Weight (Other Source) - " + immutableData["ONMC.general.OtherSourceWeightBMI"] + "kg"+ osVal + "\n" : "";
       var height = immutableData["ONMC.general.OtherSourceHeightBMI"] ?"Height (Other Source) - "+ immutableData["ONMC.general.OtherSourceHeightBMI"] + "cm"+ osVal + "\n"  : "";
       var bmi = immutableData["ONMC.general.OtherSourceValueBMI"] ?"BMI (Other Source) - "+ immutableData["ONMC.general.OtherSourceValueBMI"]  + osVal + "\n": "";
       
    
     bmiInfo = bmiInfo + weight + height + bmi;
   }
   if (bmiInfo.length > 0){
       bmiInfo = bmiInfo + "\n";
   }
    
}
var lipidSummary = immutableData["ONMC.general.lipidSummary"]  ? immutableData["ONMC.general.lipidSummary"] + "\n\n" : "";
var HbA1cSummary = immutableData["ONMC.general.HbA1cSummary"]  ? immutableData["ONMC.general.HbA1cSummary"] + "\n\n" : "";
var lipidSummaryHTML = immutableData["ONMC.general.lipidSummaryHTML"]  ? immutableData["ONMC.general.lipidSummaryHTML"] + "<br/><br/>" : "";
var HbA1cSummaryHTML = immutableData["ONMC.general.HbA1cSummaryHTML"]  ? immutableData["ONMC.general.HbA1cSummaryHTML"] + "<br/><br/>" : "";

var finalString = HbA1cSummary + lipidSummary;
finalString = finalString.replace("\n\n\n\n", "\n\n");
finalString = finalString.replace("\n\n\n", "\n\n");

var finalStringHTML = HbA1cSummaryHTML  + lipidSummaryHTML;
finalStringHTML = finalStringHTML.replace("<br/><br/><br/><br/>", "<br/><br/>");
finalStringHTML = finalStringHTML.replace("<br/><br/><br/>", "<br/><br/>");


//const nonAdherenceRows = countDTRows(immutableData, "nonAdherenceReason");
var nonAdherenceText = "Patient is non-adherent to the following medications:";
var hasNonAdherence = false;
var i = 0;
while(immutableData["medication[" + i.toString() + "].name"]){
     var containsNonAd = immutableData["medication[" + i.toString() + "].showAdherenceIssueFollowUp"] ? immutableData["medication[" + i.toString() + "].showAdherenceIssueFollowUp"][0] : undefined;
  //  if(immutableData["medication[" + i.toString() + "].showAdherenceIssueFollowUp"]){
   if (containsNonAd === "There are adherence issues") {
        hasNonAdherence = true;
        console.log("Inside of med # \t" + i );
        let medInfo = "\n\n" + immutableData["medication[" + i.toString() + "].nameAndStrength"] + "\nEducation Points: ";
        var  keyEducationPoints = immutableData["medication[" + i.toString() + "].keyEducationPoints"];
        if (keyEducationPoints === undefined ) { keyEducationPoints = [];}
        for(let j = 0; j < keyEducationPoints.length; j++){
            medInfo += "\n";
            medInfo += keyEducationPoints[j];
        }
        nonAdherenceText += medInfo;
    }
    i++;
}

console.log("finished summary and goals non-adherence text generation");

/*
var nonAdherenceMeds = [];
for (let i = 0; i < nonAdherenceRows; i++) {
   const nonAdherenceMed = immutableData["nonAdherenceReason[" + i + "].medication"] ? immutableData["nonAdherenceReason[" + i + "].medication"] : "";
   hasNonAdherence = true;
   if (nonAdherenceMed && !nonAdherenceMeds.includes(nonAdherenceMed)) {
       nonAdherenceMeds.push(nonAdherenceMed); // Add medication to the array
       nonAdherenceText += nonAdherenceMed + "\n";
       hasNonAdherence = true;
   }
}
*/

//updatedData["ONMC.summary.followUpCompiled"]=finalString;
//updatedData["ONMC.summary.OtherPleaseSpecifyCholes"]=finalString + bpInformation + bmiInfo ;
var summaryOfTodaysDiscussionTherapeutic = immutableData["summaryOfTodaysDiscussionTherapeutic"] ?  immutableData["summaryOfTodaysDiscussionTherapeutic"] + "\n" : "";
if (hasNonAdherence) {
   updatedData["ONMC.summary.InitialTextCompiled"] = finalString + bpInformation + bmiInfo  + nonAdherenceText + "\n" + summaryOfTodaysDiscussionTherapeutic ;
   var extraFieldsHTML= bpInformation + bmiInfo  + nonAdherenceText + "<br/>" + summaryOfTodaysDiscussionTherapeutic 
   extraFieldsHTML = extraFieldsHTML.replace(newLineRegex,"<br/>");
   updatedData["ONMC.summary.InitialTextCompiledHTML"] = finalStringHTML + extraFieldsHTML;

} else {
   updatedData["ONMC.summary.InitialTextCompiled"]=finalString + bpInformation + bmiInfo + summaryOfTodaysDiscussionTherapeutic;
   var extraFieldsHTML= bpInformation + bmiInfo + summaryOfTodaysDiscussionTherapeutic;
   extraFieldsHTML = extraFieldsHTML.replace(newLineRegex,"<br/>");
   updatedData["ONMC.summary.InitialTextCompiledHTML"] = finalStringHTML + extraFieldsHTML;
}

/* const summaryOfTodaysDiscussionTherapeutic = immutableData["summaryOfTodaysDiscussionTherapeutic"] ? immutableData["summaryOfTodaysDiscussionTherapeutic"] : undefined;

if (summaryOfTodaysDiscussionTherapeutic!= undefined) {
   updatedData["ONMC.summary.InitialTextCompiled"] += "\n" + summaryOfTodaysDiscussionTherapeutic;
} */ 

/*
let whatIWillDoTherapeutic = immutableData["whatIWillDoTherapeutic"] ? immutableData["whatIWillDoTherapeutic"] : undefined;
let whatIWillDo = immutableData["ONMC.summary.whatIWillDo"] ? immutableData["ONMC.summary.whatIWillDo"] : undefined;
let statementAdded = immutableData["statementAdded"] ? immutableData["statementAdded"] : false; 

console.log(whatIWillDoTherapeutic);
console.log(whatIWillDo);

if(whatIWillDoTherapeutic != undefined) {
