       updatedData["ONMC.summary.whatIWillDo"] = whatIWillDo + "\n\n" + whatIWillDoTherapeutic;
       updatedData["statementAdded"] = true;
   } else if (whatIWillDo == undefined) {
       updatedData["ONMC.summary.whatIWillDo"] = whatIWillDoTherapeutic;
   }
  
}
*/

