if(immutableData["medication[" + i.toString() + "].name"] != undefined){
   if(immutableData["followUpTopicSelection"][0] == "Non-Adherence"){
       let adherenceIssuesFollowUp = immutableData["showAdherenceIssueFollowUp"][0] != undefined ? true : false;
       updatedData["meds[" + i.toString() + "].adherenceCheck"] = adherenceIssuesFollowUp;
